-- ----------------------------
-- Table structure for news
-- ----------------------------
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) NOT NULL,
  `image` varchar(512) NOT NULL,
  `cat_id` int(11) NOT NULL,
  `sapo` varchar(512) DEFAULT NULL,
  `content` text NOT NULL,
  `active` tinyint(4) DEFAULT '1',
  `hot` tinyint(4) DEFAULT NULL,
  `create_date` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `modify_date` datetime DEFAULT NULL,
  `view` int(11) DEFAULT NULL,
  `comment` int(11) DEFAULT NULL,
  `count_like` int(11) DEFAULT NULL,
  `has_video` tinyint(4) DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `cat_id` (`cat_id`),
  CONSTRAINT `news_ibfk_1` FOREIGN KEY (`cat_id`) REFERENCES `category` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=23 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of news
-- ----------------------------
INSERT INTO `news` VALUES ('1', 'Aston Villa 1-1 Man United: Falcao ghi bàn, chơi hơn người, Man United vẫn mất điểm', '5496cea3f05dc.jpg', '6', 'Man United đã hòa 1-1 với Aston Villa ở vòng 17 Premier League . Đội bóng của HLV Louis van Gaal bị thủng lưới trước nhưng Falcao đã gỡ hòa 1-1 trong hiệp 2. Dù chơi hơn người ở phút 65 nhưng Man United không tận dụng được lợi thế này.', '<p><strong>+ Kết quả c&aacute;c trận đấu vừa diễn ra ở v&ograve;ng 17 Premier League:</strong></p>\r\n\r\n<table align=\"center\" cellpadding=\"10\" id=\"boxdoituong\" style=\"height:118px; line-height:21px; width:453px\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p>Aston Villa 1 - 1 Man Utd&nbsp;</p>\r\n\r\n			<p>Hull 0 - 1 Swansea&nbsp;</p>\r\n\r\n			<p>QPR 3 - 2 West Brom&nbsp;</p>\r\n\r\n			<p>Southampton 3 - 0 Everton&nbsp;</p>\r\n\r\n			<p>Tottenham 2 - 1 Burnley&nbsp;</p>\r\n\r\n			<p>West Ham 2 - 0 Leicester</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<div style=\"clear:both;\">&nbsp;</div>\r\n\r\n<p>+ CẬP NHẬT BXH Premier League: Man United vẫn ở vị tr&iacute; thứ 3 với 32 điểm sau 17 trận đấu. Họ k&eacute;m Chelsea v&agrave; Man City 7 điểm. West Ham xếp thứ tư v&agrave; chỉ k&eacute;m Man United 1 điểm.&nbsp;</p>\r\n\r\n<p><img src=\"http://media.thethaovanhoa.vn/2014/12/20/23/57/BXH-2.jpg\" style=\"border:none\" /><br />\r\n<br />\r\n&nbsp;</p>\r\n\r\n<p><img src=\"http://media.thethaovanhoa.vn/2014/12/20/23/54/falcao-3.jpg\" style=\"border:none\" /><br />\r\nB&agrave;n thắng của Falcao l&agrave; kh&ocirc;ng đủ để Man United gi&agrave;nh chiến thắng trước Aston Villa</p>\r\n\r\n<p>Hết giờ! Man United đ&atilde; h&ograve;a 1-1 với Aston Villa ở v&ograve;ng 17 Premier League . Đội b&oacute;ng của HLV Louis van Gaal bị thủng lưới trước nhưng Falcao đ&atilde; gỡ h&ograve;a 1-1 trong hiệp 2. D&ugrave; chơi hơn người ở ph&uacute;t 65 nhưng Man United kh&ocirc;ng tận dụng được lợi thế n&agrave;y.</p>\r\n\r\n<p>Trận đấu được b&ugrave; giờ 3 ph&uacute;t. Đ&acirc;y l&agrave; cơ hội cho c&aacute;c cầu thủ của Man United!</p>\r\n\r\n<p>87- NGUY HIỂM!&nbsp;Bacuna dứt điểm từ ngo&agrave;i v&ograve;ng cấm nhưng b&oacute;ng đi hơi cao!</p>\r\n\r\n<p>85-&nbsp;Young tạt b&oacute;ng v&agrave;o v&ograve;ng cấm cho Falcao nhưng thủ m&ocirc;n Guzan đ&atilde; bắt gọn đường b&oacute;ng của anh.&nbsp;</p>\r\n\r\n<p>78- Man United được hưởng li&ecirc;n tiếp 3 quả phạt g&oacute;c. C&aacute;c học tr&ograve; của HLV Louis Van Gaal đang g&acirc;y sức &eacute;p lớn l&ecirc;n khung th&agrave;nh của Aston Villa.&nbsp;</p>\r\n', '1', '1', '2014-12-21 00:36:38', '2014-12-23 20:48:42', '2', '0', '0', '0');
INSERT INTO `news` VALUES ('2', 'Truyền thông thế giới ca ngợi tinh thần người Thái, tiếc cho Malaysia', '5496cc8c5996f.jpg', '10', 'Các kênh truyền thông hàng đầu thế giới đã dành những lời khen ngợi cho sự trở lại ấn tượng của tuyển Thái Lan dưới thời HLV Kiatisak sau khi ngược dòng vượt qua Malaysia để đăng quang AFF Cup 2014.', '<p>B&aacute;o giới quốc tế chủ yếu d&agrave;nh nhiều lời khen ngợi cho tinh thần của c&aacute;c cầu thủ trẻ Th&aacute;i Lan ở trận chung kết lượt về diễn ra h&ocirc;m 20.12 sau khi tưởng như họ đ&atilde; gục ng&atilde; trước &aacute;p lực từgần 100.000 cổ động vi&ecirc;n&nbsp;<a href=\"http://bongda.com.vn/tags/Malaysia/\" target=\"_blank\">Malaysia</a>&nbsp;tại &ldquo;chảo lửa&rdquo; Bukit Jalil, đồng thời bị dẫn trước 3-0 cho đến ph&uacute;t 80.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, sự quyết t&acirc;m cao độ của người Th&aacute;i được đền đ&aacute;p ở 10 ph&uacute;t sau b&agrave;n r&uacute;t ngắn tỷ số xuống c&ograve;n 1-3 của Charyl Chappuis (ph&uacute;t 81), trước khi cầu thủ c&oacute; biệt danh &ldquo;Messi Jay&rdquo; Chanathip Songkrasin dập tắt hy vọng của Malaysia với b&agrave;n thắng thứ 2 cho đội kh&aacute;ch ở ph&uacute;t 88. Theo đ&oacute;, d&ugrave; thất bại với tỷ số 3-2 nhưng Th&aacute;i Lan vẫn gi&agrave;nh chức v&ocirc; địch với tổng tỷ số 4-3 nhờ thắng 2-0 ở trận lượt đi tại Bangkok.</p>\r\n\r\n<p>Cuộc ngược d&ograve;ng của tuyển Th&aacute;i Lan được h&atilde;ng tin AFP ca ngợi với d&ograve;ng t&iacute;t: &ldquo;Sự trở lại ấn tượng của Th&aacute;i Lan l&agrave;m tan n&aacute;t tr&aacute;i tim của Malaysia&rdquo;. H&atilde;ng tin n&agrave;y b&igrave;nh luận:<em>&ldquo;Một thất bại t&agrave;n nhẫn đối với Malaysia, đội đ&atilde; tho&aacute;t được cửa ải Việt Nam ở b&aacute;n kết sau khi lật ngược thế thua 1-2 ở trận lượt đi tr&ecirc;n s&acirc;n nh&agrave; để gi&agrave;nh chiến thắng 4-2 ở trận lượt về tại H&agrave; Nội.</em></p>\r\n\r\n<p><em>Đ&oacute; cũng l&agrave; một kết cục chua cay cho đội b&oacute;ng từng đăng quang AFF Cup 2010 sau khi h&igrave;nh ảnh b&oacute;ng đ&aacute; nước n&agrave;y bị hoen ố bởi sự cố cổ động vi&ecirc;n nh&agrave; tấn c&ocirc;ng người h&acirc;m mộ Việt Nam tr&ecirc;n s&acirc;n Shah Alam ở trận lượt đi b&aacute;n kết&rdquo;.</em></p>\r\n\r\n<div id=\"attachment_1366903\" style=\"margin-left:auto;clear:both;\"><img alt=\"Truyền thông thế giới ca ngợi tinh thần người Thái, tiếc cho Malaysia\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/Thailand.jpg\" style=\"border:0px none; height:auto; width:650px\" />\r\n<p style=\"margin-left:5px\">Th&aacute;i Lan (&aacute;o đỏ) v&agrave; Malaysia đ&atilde; c&oacute; một trận đấu nghẹt thở, nhiều cảm x&uacute;c &ndash; Ảnh: internet</p>\r\n</div>\r\n\r\n<p>Trong khi đ&oacute;, H&atilde;ng tin Reuters cũng d&agrave;nh những lời khen ngợi cho cuộc ngược d&ograve;ng của c&aacute;c học tr&ograve; HLV HLV Kiatisak d&ugrave; hầu hết c&ograve;n rất trẻ với d&ograve;ng t&iacute;t:&nbsp;<em>&ldquo;Th&aacute;i Lan l&agrave;m cuộc ngược d&ograve;ng muộn m&agrave;ng để lần thứ 4 đoạt chức v&ocirc; địch ASEAN Cup (AFF Cup)&rdquo;.</em></p>\r\n\r\n<p>K&ecirc;nh ESPN ca ngợi nỗ lực tuyệt vời của đội qu&acirc;n HLV Kiatisak v&agrave; cầu thủ c&oacute; biệt danh &ldquo;Messi Jay&rdquo; Chanathip Songkrasin (người sau đ&oacute; đoạt giải Cầu thủ xuất sắc nhất AFF Cup 2014) về b&agrave;n thắng đập tan hy vọng của Malaysia ở những ph&uacute;t cuối trận.<em>&ldquo;Chanathip đ&atilde; chứng tỏ ưu thế về tầm v&oacute;c v&agrave; kỹ năng dẫn b&oacute;ng lắt l&eacute;o để l&agrave;m tan n&aacute;t tr&aacute;i tim của đội chủ nh&agrave;&hellip; Chức v&ocirc; địch cũng t&ocirc;n vinh Kiatisak khi trở th&agrave;nh người đầu ti&ecirc;n đăng quang AFF Cup tr&ecirc;n cả hai cương vị cầu thủ v&agrave; HLV&rdquo;</em>, ESPN viết.</p>\r\n\r\n<p>K&ecirc;nh th&ocirc;ng tin n&agrave;y cũng kh&ocirc;ng qu&ecirc;n d&agrave;nh những d&ograve;ng luyến tiếc cho Malaysia:&nbsp;<em>&ldquo;Trận thắng 3-2 ở lượt về l&agrave; điều an ủi cho Malaysia, bởi họ vẫn giữ được th&agrave;nh t&iacute;ch bất bại trước Th&aacute;i Lan kể từ năm 1971. Malaysia đ&atilde; tiến đến rất gần danh hiệu AFF Cup 2014 v&agrave; họ c&oacute; thể tự h&agrave;o về những nỗ lực của m&igrave;nh khi đ&aacute;nh bại những cựu v&ocirc; địch Singapore, Việt Nam v&agrave; Th&aacute;i Lan tại giải đấu lớn nhất khu vực Đ&ocirc;ng Nam &Aacute;&rdquo;.</em></p>\r\n', '1', '0', '2014-12-21 20:04:52', '2014-12-30 21:54:52', '1', '0', '0', '0');
INSERT INTO `news` VALUES ('4', '5 trận đấu đáng nhớ của lứa U19 Việt Nam năm 2014', '5499778fc14af.jpg', '2', 'Có cả niềm vui chiến thắng và nỗi buồn thất bại nhưng những màn thể hiện của các cầu thủ U19 Việt Nam đều để lại ấn tượng sâu đậm trong lòng các cổ động viên.', '<p style=\"text-align:justify\"><strong>U19 Việt Nam 1 &ndash; 2&nbsp;<a href=\"http://beta.bongda.com.vn/tags/U19-AS-Roma/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">U19 AS Roma</a></strong></p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/U19vn11.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\" 5 trận đấu đáng nhớ của lứa U19 Việt Nam năm 2014 \" class=\"aligncenter size-full wp-image-1366844\" src=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/U19vn11.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; clear:both; display:block; height:auto; list-style:none; margin:2px auto; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:660px\" /></a></p>\r\n\r\n<p style=\"text-align:justify\">Giải đấu mở m&agrave;n năm 2014 của đội tuyển U19 Việt Nam l&agrave; C&uacute;p b&oacute;ng đ&aacute; Nutifood tổ chức tại TP.HCM hồi th&aacute;ng 1 với những đội b&oacute;ng kh&aacute;ch mời c&oacute; chất lượng rất cao như U19 AS Roma, U19 Tottenham Hotspur v&agrave; U19 Nhật Bản. Trận ra qu&acirc;n gặp U19 AS Roma, C&ocirc;ng Phượng, Tuấn Anh c&ugrave;ng đồng đội đ&atilde; g&acirc;y ấn tượng mạnh bằng một thế trận &aacute;p đảo trước đội trẻ của CLB danh tiếng Italy. Tuy kh&ocirc;ng thể gi&agrave;nh chiến thắng trước U19 AS Roma v&agrave; sau đ&oacute; l&agrave; 2 thất bại nặng nề trước U19 Tottenham Hotspur v&agrave; U19 Nhật Bản nhưng m&agrave;n thể hiện của U19 Việt Nam vẫn được đ&aacute;nh gi&aacute; rất cao trước những đối thủ vượt trội về đẳng cấp v&agrave; kinh nghiệm. Đ&acirc;y được xem l&agrave; cữ dượt hết sức quan trọng trước khi C&ocirc;ng Phượng, Tuấn Anh bước v&agrave;o thi đấu c&aacute;c giải khu vực v&agrave; ch&acirc;u lục trong năm 2014. Ảnh: T&ugrave;ng L&ecirc;</p>\r\n\r\n<p style=\"text-align:justify\"><em><strong>Video: Văn Sơn ghi b&agrave;n v&agrave;o lưới U19 AS Roma</strong></em></p>\r\n\r\n<div class=\"bongda_playercss\" id=\"bongda_player\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;\">\r\n<p><iframe frameborder=\"0\" height=\"465\" src=\"http://www.youtube.com/embed/gzgRlCnXZMM?feature=oembed\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border-width: 0px; border-style: none; max-width: 100%;\" width=\"620\"></iframe></p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\"><strong>U19 Việt Nam 1 &ndash; 0 U19 Th&aacute;i Lan</strong></p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/U19vn2.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\" 5 trận đấu đáng nhớ của lứa U19 Việt Nam năm 2014 \" class=\"aligncenter size-full wp-image-1366846\" src=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/U19vn2.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; clear:both; display:block; height:auto; list-style:none; margin:2px auto; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:660px\" /></a></p>\r\n\r\n<p style=\"text-align:justify\">Bất chấp việc sử dụng lối chơi th&ocirc; bạo nhằm khắc chế lối chơi kỹ thuật của c&aacute;c cầu thủ U19 Việt Nam, U19 Th&aacute;i Lan vẫn phải nhận thất bại trước c&aacute;c học tr&ograve; của HLV Graechen tại b&aacute;n kết giải U22 ĐNA Hassanal Bolkiah Trophy tổ chức ở Brunei. Thể hiện phong độ thuyết phục v&agrave; xứng đ&aacute;ng gi&agrave;nh quyền v&agrave;o chơi chung kết nhưng đ&aacute;ng tiếc l&agrave; sau đ&oacute;, C&ocirc;ng Phượng, Tuấn Anh c&ugrave;ng đồng đội đ&atilde; thất bại trước U19 Myanmar. Ảnh: T&ugrave;ng L&ecirc;</p>\r\n\r\n<p style=\"text-align:justify\"><em><strong>Highlight U19 Việt Nam 1-0 U19 Th&aacute;i Lan</strong></em></p>\r\n\r\n<div class=\"bongda_playercss\" id=\"bongda_player\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;\">\r\n<p><iframe frameborder=\"0\" height=\"349\" src=\"http://www.youtube.com/embed/y25WYuGNUfQ?feature=oembed\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border-width: 0px; border-style: none; max-width: 100%;\" width=\"620\"></iframe></p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\"><strong>U19 Việt Nam 4 &ndash; 1 U19 Myanmar</strong></p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/U19vn3.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\" 5 trận đấu đáng nhớ của lứa U19 Việt Nam năm 2014 \" class=\"aligncenter wp-image-1366847\" src=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/U19vn3.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; clear:both; display:block; height:auto; list-style:none; margin:2px auto; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:620px\" /></a></p>\r\n\r\n<p style=\"text-align:justify\">Kh&ocirc;ng l&acirc;u sau đ&oacute;, U19 Việt Nam c&oacute; m&agrave;n phục th&ugrave; ho&agrave;n hảo trước ch&iacute;nh U19 Myanmar tại giải U19 ĐNA mở rộng tổ chức ở Mỹ Đ&igrave;nh. Trong trận b&aacute;n kết, C&ocirc;ng Phượng, Tuấn Anh c&ugrave;ng đồng đội đ&atilde; đ&egrave; bẹp U19 Myanmar bằng tỷ số 4-1. Pha ghi b&agrave;n mở tỷ số trận đấu của Tuấn Anh được ca ngợi l&agrave; si&ecirc;u phẩm đạt đến đẳng cấp quốc tế. Thể hiện phong độ thuyết phục nhưng một lần nữa thầy tr&ograve; HLV Graechen lại kh&ocirc;ng thể bước l&ecirc;n ng&ocirc;i v&ocirc; địch khi thất bại trước U19 Nhật Bản trong trận chung kết. Ảnh: Tuấn Mark</p>\r\n\r\n<p style=\"text-align:justify\"><em><strong>Video: B&agrave;n thắng từ xa tuyệt đẹp của&nbsp;<a href=\"http://beta.bongda.com.vn/tags/Nguyen-Tuan-Anh/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Tuấn Anh</a></strong></em></p>\r\n\r\n<div class=\"bongda_playercss\" id=\"bongda_player\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;\">\r\n<p><iframe frameborder=\"0\" height=\"349\" src=\"http://www.youtube.com/embed/zVAGuvvEahs?feature=oembed\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border-width: 0px; border-style: none; max-width: 100%;\" width=\"620\"></iframe></p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\"><strong>U19 Việt Nam 1 &ndash; 1 U19 Trung Quốc</strong></p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/U19vn4.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\" 5 trận đấu đáng nhớ của lứa U19 Việt Nam năm 2014 \" class=\"aligncenter size-full wp-image-1366849\" src=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/U19vn4.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; clear:both; display:block; height:auto; list-style:none; margin:2px auto; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:660px\" /></a></p>\r\n\r\n<p style=\"text-align:justify\">Kh&aacute;t khao khẳng định của C&ocirc;ng Phượng, Tuấn Anh c&ugrave;ng đồng đội tại V&ograve;ng chung kết U19 ch&acirc;u &Aacute; đ&atilde; bị dội g&aacute;o nước lạnh ngay từ trận ra qu&acirc;n bằng thất bại nặng nề 0-6 trước U19 H&agrave;n Quốc. Sau đ&oacute;, thất bại 1-3 trước U19 Nhật Bản khiến U19 Việt Nam ch&iacute;nh thức bị loại khỏi giải. Tuy vậy, ở trận đấu cuối c&ugrave;ng gặp U19 Trung Quốc, U19 Việt Nam đ&atilde; thể hiện một diện mạo cực kỳ thuyết phục, chơi ho&agrave;n to&agrave;n tr&ecirc;n ch&acirc;n đối thủ v&agrave; chỉ chịu chia điểm rất đ&aacute;ng tiếc v&agrave;o những ph&uacute;t cuối c&ugrave;ng. Điều đ&oacute; cho thấy, vấn đề của U19 Việt Nam kh&ocirc;ng phải l&agrave; kỹ năng chơi b&oacute;ng m&agrave; nằm ở kinh nghiệm, bản lĩnh thi đấu c&ugrave;ng nền tảng thể lực chưa tốt của c&aacute;c cầu thủ. Sau thất bại tại V&ograve;ng chung kết U19 ch&acirc;u &Aacute;, bầu Đức đ&atilde; thể hiện kh&aacute;t khao phục th&ugrave; của &ocirc;ng bằng tuy&ecirc;n bố sẽ l&agrave;m mọi việc cần thiết để lứa C&ocirc;ng Phượng, Tuấn Anh v&ocirc; địch SEA Games 29 (2017). Ảnh: T&ugrave;ng L&ecirc;</p>\r\n\r\n<p style=\"text-align:justify\"><em><strong>Highlights U19 Việt Nam 1-1 U19 Trung Quốc</strong></em></p>\r\n\r\n<div class=\"bongda_playercss\" id=\"bongda_player\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;\">\r\n<p><iframe frameborder=\"0\" height=\"349\" src=\"http://www.youtube.com/embed/89-356RA3s8?feature=oembed\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border-width: 0px; border-style: none; max-width: 100%;\" width=\"620\"></iframe></p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\"><strong>U19 HAGL 3 &ndash; 0 U21 Th&aacute;i Lan</strong></p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/U19vn5.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\" 5 trận đấu đáng nhớ của lứa U19 Việt Nam năm 2014 \" class=\"aligncenter size-full wp-image-1366850\" src=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/U19vn5.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; clear:both; display:block; height:auto; list-style:none; margin:2px auto; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:660px\" /></a></p>\r\n\r\n<p style=\"text-align:justify\">Sau bao ng&agrave;y chờ đợi, cuối c&ugrave;ng C&ocirc;ng Phượng, Tuấn Anh c&ugrave;ng đồng đội đ&atilde; c&oacute; được danh hiệu v&ocirc; địch đầu ti&ecirc;n tại giải U21 quốc tế. Trong trận chung kết với U21 Th&aacute;i Lan, U19 HAGL đ&atilde; xuất sắc vượt qua đối thủ n&agrave;y bằng tỷ số 3-0. Qu&aacute; phấn kh&iacute;ch với chiến thằng gi&agrave;nh được, bầu Đức đ&atilde; d&ugrave;ng cả chuy&ecirc;n cơ của &ocirc;ng để rước c&uacute;p v&agrave; tổ chức lễ ăn mừng ho&agrave;nh tr&aacute;ng tại Pleiku. Đ&oacute; cũng l&agrave; th&agrave;nh quả ghi nhận sự tiến bộ kh&ocirc;ng ngừng của lứa cầu thủ kh&oacute;a 1 Học viện HAGL Arsenal JMG suốt 2 năm qua, trước khi bước v&agrave;o chinh chiến m&ugrave;a giải đầu ti&ecirc;n ở V.League. Ảnh: T&ugrave;ng L&ecirc;</p>\r\n\r\n<p style=\"text-align:justify\"><em><strong>Highlights: U19 HAGL 3-0 U21 Th&aacute;i Lan</strong></em></p>\r\n\r\n<div class=\"bongda_playercss\" id=\"bongda_player\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;\">\r\n<p><iframe frameborder=\"0\" height=\"349\" scrolling=\"no\" src=\"http://www.youtube.com/embed/KwwHmVoAB_U?feature=oembed\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border-width: 0px; border-style: none; max-width: 100%;\" width=\"620\"></iframe></p>\r\n</div>\r\n', '1', '1', '2014-12-21 20:47:30', '2014-12-23 21:09:32', '4', '0', '0', '0');
INSERT INTO `news` VALUES ('5', 'Chấm điểm sinh tử của các HLV ở Ngoại hạng Anh mùa này', '5496fa8d73184.jpg', '6', 'Sau gần nửa chặng đường mùa này, Ngoại hạng Anh tỏ ra khá êm đềm với các HLV khi chưa có ai mất việc. Nhưng rất có thể một vài người sẽ phải ra đi sau giai đoạn khốc liệt từ Giáng Sinh qua đầu năm mới.', '<div class=\"wp-caption aligncenter\" id=\"attachment_1366827\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\">\r\n<p><a href=\"http://bongda.com.vn/tags/Arsene-Wenger/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Arsene Wenger</a>: Cuộc tranh c&atilde;i hằng năm li&ecirc;n quan tới Arsenal vẫn tiếp tục n&oacute;ng với chủ đề liệu&nbsp;<a href=\"http://www.bongda.com.vn/bong-da-chau-a/arsene-wenger-4-nam-nhung-quyet-dinh-sai-lam/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\" title=\"Arsene Wenger: 4 năm &amp; những quyết định sai lầm\">CLB n&agrave;y sẽ tốt hơn hay tồi tệ hơn một khi kh&ocirc;ng c&ograve;n sự dẫn dắt của nh&agrave; cầm qu&acirc;n tại vị l&acirc;u năm nhất ở Premier League</a>. Bất chấp sự phản đối ng&agrave;y c&agrave;ng tăng từ đ&ocirc;ng đảo người h&acirc;m mộ, Wenger vẫn gi&agrave;nh được sự ủng hộ của những người quan trọng nhất trong ban l&atilde;nh đạo CLB nhờ Arsenal vẫn bảo đảm được c&aacute;c khoản thu nhập kh&ocirc;ng nhỏ từ Champions League. Bốn m&ugrave;a li&ecirc;n tiếp vừa qua chiến lược gia người Ph&aacute;p đều đưa Arsenal v&agrave;o tới v&ograve;ng 1/8 của đấu trường số một ch&acirc;u &Acirc;u, v&agrave; chỉ chịu dừng bước trước c&aacute;c đối thủ mạnh l&agrave; Milan, Barca v&agrave; Bayern Munich. M&ugrave;a n&agrave;y họ c&oacute; khả năng tiến xa hơn tại giải khi chỉ phải tranh v&eacute; v&agrave;o tứ kết với Monaco của Ph&aacute;p. V&igrave; thế, d&ugrave; Arsenal tiếp tục kh&ocirc;ng đủ sức cạnh tranh ng&ocirc;i cao ở giải Ngoại hạng Anh, cơ hội tồn tại ở Arsenal của Wenger vẫn l&agrave; 7/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366828\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 630px;\"><img alt=\"Paul Lambert: Aston Villa chỉ đứng nửa cuối bảng xếp hạng, và dường như đang bị mắc kẹt trong vết lún của bánh xe. Điều này không bao giờ là tin tốt lành với một HLV. Hiện tại, người hâm mộ không dám đặt nhiều kỳ vọng ở Villa. Bởi Lambert từng được đánh giá là một trong những nhà cầm quân trẻ tài năng nhưng cũng không làm được gì nhiều để cho thấy dấu hiệu ông có thể đưa CLB này trở lại thời huy hoàng xa xưa. HLV 45 tuổi người Scotland này từng nổi danh khi giúp Norwich City thăng hạng liên tiếp ở hai mùa giải 2009–10 và 2010–11 để có vé tham dự Ngoại hạng Anh, nhưng ông chỉ đủ sức cùng Villa đứng thứ 15 mùa trước. Tuy nhiên, nhà cầm quân trẻ này đang phải dẫn dắt đội bóng trong tình trạng bị thắt chặt chi tiêu, và ông chủ người Mỹ Randy Lerner đang cố gắng bán CLB vì vậy cần sự ổn định ở mọi khâu. Cơ hội tồn tại: 5/10.\" class=\"size-full wp-image-1366828\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene1.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:620px\" />\r\n<p>Paul Lambert: Aston Villa chỉ đứng nửa cuối bảng xếp hạng, v&agrave; dường như đang bị mắc kẹt trong vết l&uacute;n của b&aacute;nh xe. Điều n&agrave;y kh&ocirc;ng bao giờ l&agrave; tin tốt l&agrave;nh với một HLV. Hiện tại, người h&acirc;m mộ kh&ocirc;ng d&aacute;m đặt nhiều kỳ vọng ở Villa. Bởi Lambert từng được đ&aacute;nh gi&aacute; l&agrave; một trong những nh&agrave; cầm qu&acirc;n trẻ t&agrave;i năng nhưng cũng kh&ocirc;ng l&agrave;m được g&igrave; nhiều để cho thấy dấu hiệu &ocirc;ng c&oacute; thể đưa CLB n&agrave;y trở lại thời huy ho&agrave;ng xa xưa. HLV 45 tuổi người Scotland n&agrave;y từng nổi danh khi gi&uacute;p Norwich City thăng hạng li&ecirc;n tiếp ở hai m&ugrave;a giải 2009&ndash;10 v&agrave; 2010&ndash;11 để c&oacute; v&eacute; tham dự Ngoại hạng Anh, nhưng &ocirc;ng chỉ đủ sức c&ugrave;ng Villa đứng thứ 15 m&ugrave;a trước. Tuy nhi&ecirc;n, nh&agrave; cầm qu&acirc;n trẻ n&agrave;y đang phải dẫn dắt đội b&oacute;ng trong t&igrave;nh trạng bị thắt chặt chi ti&ecirc;u, v&agrave; &ocirc;ng chủ người Mỹ Randy Lerner đang cố gắng b&aacute;n CLB v&igrave; vậy cần sự ổn định ở mọi kh&acirc;u. Cơ hội tồn tại: 5/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366829\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 778px;\"><img alt=\"Sean Dyche: HLV 43 tuổi người Anh có vẻ hơi lúng túng hồi đầu mùa khi tân binh Burnley thực sự chật vật với nỗ lực làm quen cùng bầu không khí đua tranh ở Premier League. Nhưng rồi đội á quân giải hạng Nhất mùa trước đã tiến bộ nhanh chóng và giờ đứng trên nhóm ba đội cầm đèn đỏ của Ngoại hạng Anh sau 16 vòng đấu. Burnley dường như không thuộc loại CLB sẽ dễ dàng sa thải HLV này trừ phi họ cảm thấy nhất thiết phải làm vậy. Cơ hội tồn tại: 6/10.\" class=\"size-full wp-image-1366829\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene2.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:768px\" />\r\n<p>Sean Dyche: HLV 43 tuổi người Anh c&oacute; vẻ hơi l&uacute;ng t&uacute;ng hồi đầu m&ugrave;a khi t&acirc;n binh Burnley thực sự chật vật với nỗ lực l&agrave;m quen c&ugrave;ng bầu kh&ocirc;ng kh&iacute; đua tranh ở Premier League. Nhưng rồi đội &aacute; qu&acirc;n giải hạng Nhất m&ugrave;a trước đ&atilde; tiến bộ nhanh ch&oacute;ng v&agrave; giờ đứng tr&ecirc;n nh&oacute;m ba đội cầm đ&egrave;n đỏ của Ngoại hạng Anh sau 16 v&ograve;ng đấu. Burnley dường như kh&ocirc;ng thuộc loại CLB sẽ dễ d&agrave;ng sa thải HLV n&agrave;y trừ phi họ cảm thấy nhất thiết phải l&agrave;m vậy. Cơ hội tồn tại: 6/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366830\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\"><img alt=\"Jose Mourinho: Hiện tại, không có một HLV nào của làng bóng đá Anh chắc ghế hơn chiến lược gia người Bồ Đào Nha này. Chelsea hiện dẫn đầu Premier League với phong độ cao ổn định, không mấy khó khăn trong chiến dịch giành vé vào vòng đấu loại trực tiếp của Champions League, và tới giờ mới chỉ thua một trận sau 16 vòng Ngoại hạng Anh mùa này. Với tài năng và uy tín hiện có, nguy cơ mất việc sẽ chỉ tới với Mourinho một khi Chelsea cùng lúc sớm bị loại ở Champions League và mất ngôi đầu Ngoại hạng Anh mùa này. Cơ hội tồn tại: 10/10.\" class=\"size-full wp-image-1366830\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene3.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:700px\" />\r\n<p>Jose Mourinho: Hiện tại, kh&ocirc;ng c&oacute; một HLV n&agrave;o của l&agrave;ng b&oacute;ng đ&aacute; Anh chắc ghế hơn chiến lược gia người Bồ Đ&agrave;o Nha n&agrave;y. Chelsea hiện dẫn đầu Premier League với phong độ cao ổn định, kh&ocirc;ng mấy kh&oacute; khăn trong chiến dịch gi&agrave;nh v&eacute; v&agrave;o v&ograve;ng đấu loại trực tiếp của Champions League, v&agrave; tới giờ mới chỉ thua một trận sau 16 v&ograve;ng Ngoại hạng Anh m&ugrave;a n&agrave;y. Với t&agrave;i năng v&agrave; uy t&iacute;n hiện c&oacute;, nguy cơ mất việc sẽ chỉ tới với Mourinho một khi Chelsea c&ugrave;ng l&uacute;c sớm bị loại ở Champions League v&agrave; mất ng&ocirc;i đầu Ngoại hạng Anh m&ugrave;a n&agrave;y. Cơ hội tồn tại: 10/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366831\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 790px;\"><img alt=\"Neil Warnock: Người chiến binh già đã trở lại với một trong những CLB cũ của ông - Crystal Palace, ngay sau khi Tony Pulis từ chức trước mùa giải mới. Chiến lược gia 66 tuổi người Anh không phải là cái tên quyến rũ với các CLB, nhưng có thể giúp các đội bóng an toàn với chiến dịch riêng của họ. Crystal Palace đã thi đấu tương đối ổn cho tới lúc này, cho dù phía trước Warnock vẫn còn lại hơn nửa mùa giải đầy khó khăn. Cơ hội tồn tại: 5/10\" class=\"size-full wp-image-1366831\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene4.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:780px\" />\r\n<p>Neil Warnock: Người chiến binh gi&agrave; đ&atilde; trở lại với một trong những CLB cũ của &ocirc;ng &ndash; Crystal Palace, ngay sau khi Tony Pulis từ chức trước m&ugrave;a giải mới. Chiến lược gia 66 tuổi người Anh kh&ocirc;ng phải l&agrave; c&aacute;i t&ecirc;n quyến rũ với c&aacute;c CLB, nhưng c&oacute; thể gi&uacute;p c&aacute;c đội b&oacute;ng an to&agrave;n với chiến dịch ri&ecirc;ng của họ. Crystal Palace đ&atilde; thi đấu tương đối ổn cho tới l&uacute;c n&agrave;y, cho d&ugrave; ph&iacute;a trước Warnock vẫn c&ograve;n lại hơn nửa m&ugrave;a giải đầy kh&oacute; khăn. Cơ hội tồn tại: 5/10</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366832\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 625px;\"><img alt=\"Roberto Martinez: Nhà cầm quân 41 tuổi người Tây Ban Nha đang cảm thấy mọi thứ trở nên khó khăn hơn một chút trong mùa giải thứ hai dẫn dắt Everton. Mùa trước CLB này kết thúc ở vị trí thứ năm Ngoại hạng Anh, còn hiện tại họ chỉ xếp thứ mười. Tuy nhiên, Martinez vẫn có nhiều điểm tích cực để làm cơ sở trụ vững ở sân Goodison Park. Đội bóng của ông tạo được phong cách riêng, ông cũng gây ấn tượng khi dũng cảm đôn nhiều cầu thủ trẻ lên đội hình một. Everton mùa này cũng đã vượt qua vòng bảng Europa League. Cơ hội tồn tại: 8/10.\" class=\"size-full wp-image-1366832\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene5.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:615px\" />\r\n<p>Roberto Martinez: Nh&agrave; cầm qu&acirc;n 41 tuổi người T&acirc;y Ban Nha đang cảm thấy mọi thứ trở n&ecirc;n kh&oacute; khăn hơn một ch&uacute;t trong m&ugrave;a giải thứ hai dẫn dắt Everton. M&ugrave;a trước CLB n&agrave;y kết th&uacute;c ở vị tr&iacute; thứ năm Ngoại hạng Anh, c&ograve;n hiện tại họ chỉ xếp thứ mười. Tuy nhi&ecirc;n, Martinez vẫn c&oacute; nhiều điểm t&iacute;ch cực để l&agrave;m cơ sở trụ vững ở s&acirc;n Goodison Park. Đội b&oacute;ng của &ocirc;ng tạo được phong c&aacute;ch ri&ecirc;ng, &ocirc;ng cũng g&acirc;y ấn tượng khi dũng cảm đ&ocirc;n nhiều cầu thủ trẻ l&ecirc;n đội h&igrave;nh một. Everton m&ugrave;a n&agrave;y cũng đ&atilde; vượt qua v&ograve;ng bảng Europa League. Cơ hội tồn tại: 8/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366833\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\"><img alt=\"Steve Bruce: HLV 53 tuổi người Anh từng nói rằng ông hiện có trong tay đội hình tốt nhất tại HullCity. Nhưng đội bóng hiện chỉ đứng áp chót bảng xếp hạng, và ông đã phải đối mặt với nhiều chất vấn. Hull City đã không có được sự gắn kết cần thiết sau một kỳ chuyển nhượng mùa hè bận rộn. Họ sở hữu một đội bóng có vẻ mạnh hơn về lý thuyết, nhưng đã phải chịu những kết quả tồi tệ hơn mùa giải trước. “Những chú hổ” trụ hạng cuối mùa bóng trước ở vị trí thứ 16, còn hiện giờ họ đứng thứ 19. Cơ hội tồn tại: 5/10.\" class=\"size-full wp-image-1366833\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene6.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:700px\" />\r\n<p>Steve Bruce: HLV 53 tuổi người Anh từng n&oacute;i rằng &ocirc;ng hiện c&oacute; trong tay đội h&igrave;nh tốt nhất tại HullCity. Nhưng đội b&oacute;ng hiện chỉ đứng &aacute;p ch&oacute;t bảng xếp hạng, v&agrave; &ocirc;ng đ&atilde; phải đối mặt với nhiều chất vấn. Hull City đ&atilde; kh&ocirc;ng c&oacute; được sự gắn kết cần thiết sau một kỳ chuyển nhượng m&ugrave;a h&egrave; bận rộn. Họ sở hữu một đội b&oacute;ng c&oacute; vẻ mạnh hơn về l&yacute; thuyết, nhưng đ&atilde; phải chịu những kết quả tồi tệ hơn m&ugrave;a giải trước. &ldquo;Những ch&uacute; hổ&rdquo; trụ hạng cuối m&ugrave;a b&oacute;ng trước ở vị tr&iacute; thứ 16, c&ograve;n hiện giờ họ đứng thứ 19. Cơ hội tồn tại: 5/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366834\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\"><img alt=\"Nigel Pearson: HLV 51 tuổi người vùng Yorkshire đã giữ được ghế tại Leicester sau mùa giải trước nhờ đưa được CLB này thăng hạng Premier League với tư cách đội vô địch hạng Nhất. Nhưng hiện giờ ông không chứng tỏ được điều gì đủ sức thuyết phục rằng nhiều khả năng ông sẽ yên vị ở CLB này. “Những chú cáo” hiện đứng đáy bảng xếp hạng, trong khi ông chủ người Thái Lan muốn CLB này tiếp tục góp mặt ở giải Ngoại hạng Anh hơn bất cứ điều gì khác. Quyết định sa thải Pearson xem ra chỉ còn là vấn đề thời gian. Cơ hội tồn tại: 1/10.\" class=\"size-full wp-image-1366834\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene7.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:700px\" />\r\n<p>Nigel Pearson: HLV 51 tuổi người v&ugrave;ng Yorkshire đ&atilde; giữ được ghế tại Leicester sau m&ugrave;a giải trước nhờ đưa được CLB n&agrave;y thăng hạng Premier League với tư c&aacute;ch đội v&ocirc; địch hạng Nhất. Nhưng hiện giờ &ocirc;ng kh&ocirc;ng chứng tỏ được điều g&igrave; đủ sức thuyết phục rằng nhiều khả năng &ocirc;ng sẽ y&ecirc;n vị ở CLB n&agrave;y. &ldquo;Những ch&uacute; c&aacute;o&rdquo; hiện đứng đ&aacute;y bảng xếp hạng, trong khi &ocirc;ng chủ người Th&aacute;i Lan muốn CLB n&agrave;y tiếp tục g&oacute;p mặt ở giải Ngoại hạng Anh hơn bất cứ điều g&igrave; kh&aacute;c. Quyết định sa thải Pearson xem ra chỉ c&ograve;n l&agrave; vấn đề thời gian. Cơ hội tồn tại: 1/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366835\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\"><img alt=\"Brendan Rodgers: Nhà cầm quân 41 tuổi người Bắc Ireland giống như món đồ thời trang được nhiều CLB thèm muốn cách đây vài tháng, khi có công dẫn dắt Liverpool giành ngôi á quân Ngoại hạng Anh. Nhưng giờ thì ông đã bắt đầu nhìn thấy nguy cơ về một mùa giải cuối cùng tại CLB giàu truyền thống này. Liverpool đã phải dừng bước ngay tại vòng bảng Champions League khi xếp thứ ba, sau cả Basel của Thụy Sĩ. Ở đấu trường nội địa, họ cũng để thua cả những đội bóng bình thường, và hiện chỉ xếp giữa bảng xếp hạng Premier League. Những yếu kém trong khâu phòng thủ mùa này của họ xem ra cũng chưa tìm được cách giải quyết, điều lộ rõ ngay trong các buổi tập. Mặc dù họ mới tránh được cú ngã tại Bournemouth để tiến vào bán kết giải đấu không mấy được coi trọng là League Cup, Brendan Rodgers vẫn đối mặt nhiều chỉ trích. Cơ hội tồn tại: 6/10.\" class=\"size-full wp-image-1366835\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene8.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:700px\" />\r\n<p>Brendan Rodgers: Nh&agrave; cầm qu&acirc;n 41 tuổi người Bắc Ireland giống như m&oacute;n đồ thời trang được nhiều CLB th&egrave;m muốn c&aacute;ch đ&acirc;y v&agrave;i th&aacute;ng, khi c&oacute; c&ocirc;ng dẫn dắt Liverpool gi&agrave;nh ng&ocirc;i &aacute; qu&acirc;n Ngoại hạng Anh. Nhưng giờ th&igrave; &ocirc;ng đ&atilde; bắt đầu nh&igrave;n thấy nguy cơ về một m&ugrave;a giải cuối c&ugrave;ng tại CLB gi&agrave;u truyền thống n&agrave;y. Liverpool đ&atilde; phải dừng bước ngay tại v&ograve;ng bảng Champions League khi xếp thứ ba, sau cả Basel của Thụy Sĩ. Ở đấu trường nội địa, họ cũng để thua cả những đội b&oacute;ng b&igrave;nh thường, v&agrave; hiện chỉ xếp giữa bảng xếp hạng Premier League. Những yếu k&eacute;m trong kh&acirc;u ph&ograve;ng thủ m&ugrave;a n&agrave;y của họ xem ra cũng chưa t&igrave;m được c&aacute;ch giải quyết, điều lộ r&otilde; ngay trong c&aacute;c buổi tập. Mặc d&ugrave; họ mới tr&aacute;nh được c&uacute; ng&atilde; tại Bournemouth để tiến v&agrave;o b&aacute;n kết giải đấu kh&ocirc;ng mấy được coi trọng l&agrave; League Cup, Brendan Rodgers vẫn đối mặt nhiều chỉ tr&iacute;ch. Cơ hội tồn tại: 6/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366836\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\"><img alt=\"Manuel Pellegrini: HLV người Chile sẽ luôn phải đối mặt nguy cơ trở thành một nạn nhân của sự kỳ vọng cao tại một CLB nhà giàu như Man City, nhưng ông đang xử lý thách thức đủ tốt. Chiến thắng sống còn ngay trên sân của Roma ở lượt cuối vòng bảng đã cứu vớt Man City trong chiến dịch Champions League mùa này, và đó là điều gần như bắt buộc để Pellegrini có thể trụ lại lâu hơn ở đây. Man City cũng đã thu hẹp cách biệt với đội dẫn đầu Chelsea xuống còn ba điểm tại Ngoại hạng Anh, nhưng mục tiêu của họ ở đấu trường này là bảo vệ chức vô địch. Cơ hội tồn tại: 7/10.\" class=\"size-full wp-image-1366836\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene9.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:700px\" />\r\n<p>Manuel Pellegrini: HLV người Chile sẽ lu&ocirc;n phải đối mặt nguy cơ trở th&agrave;nh một nạn nh&acirc;n của sự kỳ vọng cao tại một CLB nh&agrave; gi&agrave;u như Man City, nhưng &ocirc;ng đang xử l&yacute; th&aacute;ch thức đủ tốt. Chiến thắng sống c&ograve;n ngay tr&ecirc;n s&acirc;n của Roma ở lượt cuối v&ograve;ng bảng đ&atilde; cứu vớt Man City trong chiến dịch Champions League m&ugrave;a n&agrave;y, v&agrave; đ&oacute; l&agrave; điều gần như bắt buộc để Pellegrini c&oacute; thể trụ lại l&acirc;u hơn ở đ&acirc;y. Man City cũng đ&atilde; thu hẹp c&aacute;ch biệt với đội dẫn đầu Chelsea xuống c&ograve;n ba điểm tại Ngoại hạng Anh, nhưng mục ti&ecirc;u của họ ở đấu trường n&agrave;y l&agrave; bảo vệ chức v&ocirc; địch. Cơ hội tồn tại: 7/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366837\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 640px;\"><img alt=\"Louis van Gaal: Chiến lược gia người Hà Lan đã gặp không ít vấn đề ở đầu mùa giải khi cố gắng sắp xếp lại đống hỗn độn kế thừa sau mùa giải thất vọng toàn diện dưới thời Moyes. Ông còn phải đối mặt với danh sách chấn thương dài và có lúc tưởng như làm tê liệt đội hình. Cũng đã xuất hiện nghi ngờ về những cách ông khắc phục yếu kém trong phòng ngự. Nhưng Van Gaal đã cho thấy sự tự tin quen thuộc. Ông chưa khi nào lộ vẻ bối rối trên băng ghế chỉ đạo của Man Utd, và giờ đã bắt đầu thể hiện rõ vì sao CLB chủ sân Old Trafford quyết định bổ nhiệm ông bằng mọi giá. Mục tiêu trước mùa giải này của Man Utd là một vị trí trong top bốn để có vé dự Champions League mùa tới, và họ hiện tại đứng thứ ba, thậm chí còn khiến Chelsea cùng Man City có lý do để lo ngại trong cuộc đua tới ngôi cao. Cơ hội tồn tại: 9/10.\" class=\"size-full wp-image-1366837\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene10.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:630px\" />\r\n<p>Louis van Gaal: Chiến lược gia người H&agrave; Lan đ&atilde; gặp kh&ocirc;ng &iacute;t vấn đề ở đầu m&ugrave;a giải khi cố gắng sắp xếp lại đống hỗn độn kế thừa sau m&ugrave;a giải thất vọng to&agrave;n diện dưới thời Moyes. &Ocirc;ng c&ograve;n phải đối mặt với danh s&aacute;ch chấn thương d&agrave;i v&agrave; c&oacute; l&uacute;c tưởng như l&agrave;m t&ecirc; liệt đội h&igrave;nh. Cũng đ&atilde; xuất hiện nghi ngờ về những c&aacute;ch &ocirc;ng khắc phục yếu k&eacute;m trong ph&ograve;ng ngự. Nhưng Van Gaal đ&atilde; cho thấy sự tự tin quen thuộc. &Ocirc;ng chưa khi n&agrave;o lộ vẻ bối rối tr&ecirc;n băng ghế chỉ đạo của Man Utd, v&agrave; giờ đ&atilde; bắt đầu thể hiện r&otilde; v&igrave; sao CLB chủ s&acirc;n Old Trafford quyết định bổ nhiệm &ocirc;ng bằng mọi gi&aacute;. Mục ti&ecirc;u trước m&ugrave;a giải n&agrave;y của Man Utd l&agrave; một vị tr&iacute; trong top bốn để c&oacute; v&eacute; dự Champions League m&ugrave;a tới, v&agrave; họ hiện tại đứng thứ ba, thậm ch&iacute; c&ograve;n khiến Chelsea c&ugrave;ng Man City c&oacute; l&yacute; do để lo ngại trong cuộc đua tới ng&ocirc;i cao. Cơ hội tồn tại: 9/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366838\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\"><img alt=\"Alan Pardew: Đa số các ông chủ sẽ sa thải một HLV sau khi đã phải chứng kiến CLB trải qua những màn trình diễn thất vọng và chịu kết quả yếu kém suốt hơn mười tháng, nhưng riêng tỷ phú người Anh Mike Ashley đã từ chối nghe theo sự phản đối của các cổ động viên. Newcastle đã cải thiện tình hình một cách rõ ràng trong hai tháng qua. Họ mới thua Tottenham ở tứ kết League Cup, nhưng ở Ngoại hạng Anh CLB này đã vươn lên vị trí thứ tám và còn nguyên cơ hội cạnh tranh một vé dự Europa League mùa tới. Pardew nhiều khả năng sẽ chỉ bị sa thải nếu Newcastle kết thúc Premier League mùa này ngoài top 10. Cơ hội tồn tại: 6/10.\" class=\"size-full wp-image-1366838\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene11.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:700px\" />\r\n<p>Alan Pardew: Đa số c&aacute;c &ocirc;ng chủ sẽ sa thải một HLV sau khi đ&atilde; phải chứng kiến CLB trải qua những m&agrave;n tr&igrave;nh diễn thất vọng v&agrave; chịu kết quả yếu k&eacute;m suốt hơn mười th&aacute;ng, nhưng ri&ecirc;ng tỷ ph&uacute; người Anh Mike Ashley đ&atilde; từ chối nghe theo sự phản đối của c&aacute;c cổ động vi&ecirc;n. Newcastle đ&atilde; cải thiện t&igrave;nh h&igrave;nh một c&aacute;ch r&otilde; r&agrave;ng trong hai th&aacute;ng qua. Họ mới thua Tottenham ở tứ kết League Cup, nhưng ở Ngoại hạng Anh CLB n&agrave;y đ&atilde; vươn l&ecirc;n vị tr&iacute; thứ t&aacute;m v&agrave; c&ograve;n nguy&ecirc;n cơ hội cạnh tranh một v&eacute; dự Europa League m&ugrave;a tới. Pardew nhiều khả năng sẽ chỉ bị sa thải nếu Newcastle kết th&uacute;c Premier League m&ugrave;a n&agrave;y ngo&agrave;i top 10. Cơ hội tồn tại: 6/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366839\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\"><img alt=\"Harry Redknapp: Nhà cầm quân 67 tuổi người London là một thành viên khác thuộc nhóm những HLV của trường phái cũ phải chiến đấu để giữ được ghế. QPR đã cho thấy một chút tiến bộ thời gian gần đây, nhưng thành tích sân khách của họ rất tệ và họ đã nằm yên ở nhóm ba đội cầm đèn đỏ trong gần nửa mùa giải hiện tại. CLB này không muốn lại phải xuống hạng, và ban lãnh đạo có thể sẽ phản ứng sớm vào đầu năm 2015 nếu tình hình không được cải thiện ở những vòng đầu tới. Cơ hội tồn tại: 2/10.\" class=\"size-full wp-image-1366839\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene12.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:700px\" />\r\n<p>Harry Redknapp: Nh&agrave; cầm qu&acirc;n 67 tuổi người London l&agrave; một th&agrave;nh vi&ecirc;n kh&aacute;c thuộc nh&oacute;m những HLV của trường ph&aacute;i cũ phải chiến đấu để giữ được ghế. QPR đ&atilde; cho thấy một ch&uacute;t tiến bộ thời gian gần đ&acirc;y, nhưng th&agrave;nh t&iacute;ch s&acirc;n kh&aacute;ch của họ rất tệ v&agrave; họ đ&atilde; nằm y&ecirc;n ở nh&oacute;m ba đội cầm đ&egrave;n đỏ trong gần nửa m&ugrave;a giải hiện tại. CLB n&agrave;y kh&ocirc;ng muốn lại phải xuống hạng, v&agrave; ban l&atilde;nh đạo c&oacute; thể sẽ phản ứng sớm v&agrave;o đầu năm 2015 nếu t&igrave;nh h&igrave;nh kh&ocirc;ng được cải thiện ở những v&ograve;ng đầu tới. Cơ hội tồn tại: 2/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366840\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\"><img alt=\"Ronald Koeman: HLV 51 tuổi người Hà Lan dường như có bàn tay biến mọi thứ thành vàng giống của vua Midas khi giúp Southampton khởi đầu mùa giải này theo cách không thể ấn tượng hơn. Họ từng có những vòng đấu thành công liên tiếp, từng đứng thứ hai sau Chelsea, rồi thứ ba trên cả Man Utd. Nhưng rồi theo thời gian, các bánh của chiếc xe Southampton đã bắt đầu gặp vấn đề khi vận hành. Điểm yếu về chiều sâu đội hình cũng ngày càng bộc lộ rõ trước các thách thức ngày một khắc nghiệt. Thất bại trước Sheffield United tại tứ kết League Cup mới đây là một đòn đau giáng vào Southampton, khi đây đã là trận thua thứ năm liền mạch của họ trên các đấu trường. Riêng tại Premier League, CLB này đã tụt xuống thứ năm. Koeman cần phải sớm dừng được tình trạng xuống dốc không phanh này nếu muốn cứu vãn mùa giải đầu tiên của ông tại Southampton. Cơ hội tồn tại: 7/10.\" class=\"size-full wp-image-1366840\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene13.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:700px\" />\r\n<p>Ronald Koeman: HLV 51 tuổi người H&agrave; Lan dường như c&oacute; b&agrave;n tay biến mọi thứ th&agrave;nh v&agrave;ng giống của vua Midas khi gi&uacute;p Southampton khởi đầu m&ugrave;a giải n&agrave;y theo c&aacute;ch kh&ocirc;ng thể ấn tượng hơn. Họ từng c&oacute; những v&ograve;ng đấu th&agrave;nh c&ocirc;ng li&ecirc;n tiếp, từng đứng thứ hai sau Chelsea, rồi thứ ba tr&ecirc;n cả Man Utd. Nhưng rồi theo thời gian, c&aacute;c b&aacute;nh của chiếc xe Southampton đ&atilde; bắt đầu gặp vấn đề khi vận h&agrave;nh. Điểm yếu về chiều s&acirc;u đội h&igrave;nh cũng ng&agrave;y c&agrave;ng bộc lộ r&otilde; trước c&aacute;c th&aacute;ch thức ng&agrave;y một khắc nghiệt. Thất bại trước Sheffield United tại tứ kết League Cup mới đ&acirc;y l&agrave; một đ&ograve;n đau gi&aacute;ng v&agrave;o Southampton, khi đ&acirc;y đ&atilde; l&agrave; trận thua thứ năm liền mạch của họ tr&ecirc;n c&aacute;c đấu trường. Ri&ecirc;ng tại Premier League, CLB n&agrave;y đ&atilde; tụt xuống thứ năm. Koeman cần phải sớm dừng được t&igrave;nh trạng xuống dốc kh&ocirc;ng phanh n&agrave;y nếu muốn cứu v&atilde;n m&ugrave;a giải đầu ti&ecirc;n của &ocirc;ng tại Southampton. Cơ hội tồn tại: 7/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366841\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\"><img alt=\"Mark Hughes: HLV 51 tuổi người xứ Wales vẫn chưa đạt được những gì đúng với kỳ vọng như khi CLB này đưa ông về dẫn dắt. Lãnh đạo CLB muốn Stoke City tăng đẳng cấp lên một bậc, xét trên khía cạnh những gì họ phải chiến đấu vất vả đề giành được mỗi mùa giải. Nhưng dù sao ông cũng đã giúp đội bóng có được thành tích tương đối ổn định ở khoảng giữa bảng xếp hạng, và chơi thứ bóng đá tương đối vừa mắt người xem. Một mùa giải nữa kết thúc ở vị trí giữa bảng cũng là kết quả tương đối hạnh phúc với một CLB có quy mô tầm như Stoke. Cơ hội tồn tại: 6/10.\" class=\"size-full wp-image-1366841\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene14.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:700px\" />\r\n<p>Mark Hughes: HLV 51 tuổi người xứ Wales vẫn chưa đạt được những g&igrave; đ&uacute;ng với kỳ vọng như khi CLB n&agrave;y đưa &ocirc;ng về dẫn dắt. L&atilde;nh đạo CLB muốn Stoke City tăng đẳng cấp l&ecirc;n một bậc, x&eacute;t tr&ecirc;n kh&iacute;a cạnh những g&igrave; họ phải chiến đấu vất vả đề gi&agrave;nh được mỗi m&ugrave;a giải. Nhưng d&ugrave; sao &ocirc;ng cũng đ&atilde; gi&uacute;p đội b&oacute;ng c&oacute; được th&agrave;nh t&iacute;ch tương đối ổn định ở khoảng giữa bảng xếp hạng, v&agrave; chơi thứ b&oacute;ng đ&aacute; tương đối vừa mắt người xem. Một m&ugrave;a giải nữa kết th&uacute;c ở vị tr&iacute; giữa bảng cũng l&agrave; kết quả tương đối hạnh ph&uacute;c với một CLB c&oacute; quy m&ocirc; tầm như Stoke. Cơ hội tồn tại: 6/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366842\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\"><img alt=\"Gus Poyet: Lãnh đạo CLB Sunderland đã nhiều lần nhấn mạnh họ muốn sự ổn định nơi ban huấn luyện, nhưng duy trì mối quan hệ với một người cá tính như HLV 47 tuổi người Uruguay không phải luôn dễ dàng. Gus Poyet cùng Sunderland thoát hiểm ngoạn mục ở mùa giải trước khi trụ hạng thành công ở vị trí thứ 14. Nhưng trong khi ông biến đội bóng của mình thành tập thể rất khó để đánh bại, thì ông và các học trò cũng luôn cảm thấy khó khăn khi muốn thắng bất cứ đội nào. Hiện tại chỉ đứng thứ 15, “Mèo đen” nhiều khả năng sẽ lại phải trải qua một cuộc chiến trụ hạng vất vả nữa. Nhưng nhiều khả năng, nếu kết quả không như ý, HLV của họ sẽ từ chức thay vì bị sa thải. Cơ hội tồn tại: 5/10.\" class=\"size-full wp-image-1366842\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene15.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:700px\" />\r\n<p>Gus Poyet: L&atilde;nh đạo CLB Sunderland đ&atilde; nhiều lần nhấn mạnh họ muốn sự ổn định nơi ban huấn luyện, nhưng duy tr&igrave; mối quan hệ với một người c&aacute; t&iacute;nh như HLV 47 tuổi người Uruguay kh&ocirc;ng phải lu&ocirc;n dễ d&agrave;ng. Gus Poyet c&ugrave;ng Sunderland tho&aacute;t hiểm ngoạn mục ở m&ugrave;a giải trước khi trụ hạng th&agrave;nh c&ocirc;ng ở vị tr&iacute; thứ 14. Nhưng trong khi &ocirc;ng biến đội b&oacute;ng của m&igrave;nh th&agrave;nh tập thể rất kh&oacute; để đ&aacute;nh bại, th&igrave; &ocirc;ng v&agrave; c&aacute;c học tr&ograve; cũng lu&ocirc;n cảm thấy kh&oacute; khăn khi muốn thắng bất cứ đội n&agrave;o. Hiện tại chỉ đứng thứ 15, &ldquo;M&egrave;o đen&rdquo; nhiều khả năng sẽ lại phải trải qua một cuộc chiến trụ hạng vất vả nữa. Nhưng nhiều khả năng, nếu kết quả kh&ocirc;ng như &yacute;, HLV của họ sẽ từ chức thay v&igrave; bị sa thải. Cơ hội tồn tại: 5/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366843\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\"><img alt=\"Garry Monk: Quyết định bổ nhiệm đội trưởng làm HLV trưởng của đội bóng sau chỉ một thời gian ngắn làm HLV tạm quyền giống như một canh bạc đầy mạo hiểm với một CLB thi đấu ở giải Premier League, nhưng Swansea đã được đền đáp xứng đáng khi đặt niềm tin vào HLV 35 tuổi người Anh kể từ hồi 2/2014, sau khi sa thải Michael Laudrup. Sau khi đứng thứ 12 giải Ngoại hạng Anh mùa trước, CLB này tiếp tục thách thức các đội có ngân sách lớn hơn họ để có được vị trí thứ chín hiện tại. Garry Monk đã thực hiện tuyệt vời công việc đầy khó khăn. Cơ hội tồn tại: 8/10.\" class=\"size-full wp-image-1366843\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene16.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:700px\" />\r\n<p>Garry Monk: Quyết định bổ nhiệm đội trưởng l&agrave;m HLV trưởng của đội b&oacute;ng sau chỉ một thời gian ngắn l&agrave;m HLV tạm quyền giống như một canh bạc đầy mạo hiểm với một CLB thi đấu ở giải Premier League, nhưng Swansea đ&atilde; được đền đ&aacute;p xứng đ&aacute;ng khi đặt niềm tin v&agrave;o HLV 35 tuổi người Anh kể từ hồi 2/2014, sau khi sa thải Michael Laudrup. Sau khi đứng thứ 12 giải Ngoại hạng Anh m&ugrave;a trước, CLB n&agrave;y tiếp tục th&aacute;ch thức c&aacute;c đội c&oacute; ng&acirc;n s&aacute;ch lớn hơn họ để c&oacute; được vị tr&iacute; thứ ch&iacute;n hiện tại. Garry Monk đ&atilde; thực hiện tuyệt vời c&ocirc;ng việc đầy kh&oacute; khăn. Cơ hội tồn tại: 8/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366845\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 630px;\"><img alt=\"Mauricio Pochettino: Trước đây chưa có HLV nào được bảo đảm an toàn khi dẫn dắtTottenham, nhưng lần này có vẻ như lãnh đạo CLB thành London này muốn kiên nhẫn hơn với Pochettino khi ký hẳn một bản hợp đồng năm năm hồi tháng 5/2014 với HLV 42 tuổi người Argentina. Hiện tại chỉ đứng thứ bảy giải Ngoại hạng Anh, nhưng ngay cả nếu Tottenham không giành được vé dự Champions League khi kết thúc mùa giải này, Mauricio Pochettino có lẽ cũng không bị sa thải. Cơ hội tồn tại: 7/10.\" class=\"size-full wp-image-1366845\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene17.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:620px\" />\r\n<p>Mauricio Pochettino: Trước đ&acirc;y chưa c&oacute; HLV n&agrave;o được bảo đảm an to&agrave;n khi dẫn dắtTottenham, nhưng lần n&agrave;y c&oacute; vẻ như l&atilde;nh đạo CLB th&agrave;nh London n&agrave;y muốn ki&ecirc;n nhẫn hơn với Pochettino khi k&yacute; hẳn một bản hợp đồng năm năm hồi th&aacute;ng 5/2014 với HLV 42 tuổi người Argentina. Hiện tại chỉ đứng thứ bảy giải Ngoại hạng Anh, nhưng ngay cả nếu Tottenham kh&ocirc;ng gi&agrave;nh được v&eacute; dự Champions League khi kết th&uacute;c m&ugrave;a giải n&agrave;y, Mauricio Pochettino c&oacute; lẽ cũng kh&ocirc;ng bị sa thải. Cơ hội tồn tại: 7/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366848\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\"><img alt=\"Alan Irvine: HLV 56 tuổi người Scotland tuyên bố chưa nhận được bất kỳ cảnh báo nào về tương lai của mình tại West Brom, nhưng điều đó không có nghĩa là ông chắc chắn không bị mất việc. Irvine hiện tại mới chỉ làm đủ để khiến mọi người hài lòng khi CLB giành được vị trí thứ 14, nhưng ông chưa đủ sức thuyết phục được mọi người rằng West Brom sẽ không phải đối mặt với cuộc chiến trụ hạng khó khăn từ giờ cho tới tháng năm. Mùa trước CLB này chỉ đứng thứ 17. Cơ hội tồn tại: 4/10.\" class=\"size-full wp-image-1366848\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene18.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:700px\" />\r\n<p>Alan Irvine: HLV 56 tuổi người Scotland tuy&ecirc;n bố chưa nhận được bất kỳ cảnh b&aacute;o n&agrave;o về tương lai của m&igrave;nh tại West Brom, nhưng điều đ&oacute; kh&ocirc;ng c&oacute; nghĩa l&agrave; &ocirc;ng chắc chắn kh&ocirc;ng bị mất việc. Irvine hiện tại mới chỉ l&agrave;m đủ để khiến mọi người h&agrave;i l&ograve;ng khi CLB gi&agrave;nh được vị tr&iacute; thứ 14, nhưng &ocirc;ng chưa đủ sức thuyết phục được mọi người rằng West Brom sẽ kh&ocirc;ng phải đối mặt với cuộc chiến trụ hạng kh&oacute; khăn từ giờ cho tới th&aacute;ng năm. M&ugrave;a trước CLB n&agrave;y chỉ đứng thứ 17. Cơ hội tồn tại: 4/10.</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1366851\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 710px;\"><img alt=\"Sam Allardyce: Từ chỗ đứng trước nguy cơ bị sa thải hồi cuối mùa trước khi West Ham chỉ kết thúc ở vị trí thứ 13, giờ đây HLV 60 tuổi người Anh xứng đáng được uống rượu mừng với vị trí thứ bốn sau 16 vòng đầu tiên của mùa này. Họ hiện chỉ kém Man Utd đúng ba điểm, đứng trên cả hai CLB giàu truyền thống là Arsenal lẫn Liverpool. Sam Allardyce sẽ không bao giờ được coi là HLV hoàn hảo của West Ham, bởi đây là CLB rất đề cao phong cách chơi bóng. Nhưng chỉ cần ông giúp đội bóng đạt được thành tích tốt như những gì đã thể hiện trong vòng bốn tháng qua ở giải Ngoại hạng Anh, sân Upton Park chắc chắn sẽ im không một tiếng phản đối. Cơ hội tồn tại: 8/10.\" class=\"size-full wp-image-1366851\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/Arsene19.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:700px\" />\r\n<p>Sam Allardyce: Từ chỗ đứng trước nguy cơ bị sa thải hồi cuối m&ugrave;a trước khi West Ham chỉ kết th&uacute;c ở vị tr&iacute; thứ 13, giờ đ&acirc;y HLV 60 tuổi người Anh xứng đ&aacute;ng được uống rượu mừng với vị tr&iacute; thứ bốn sau 16 v&ograve;ng đầu ti&ecirc;n của m&ugrave;a n&agrave;y. Họ hiện chỉ k&eacute;m Man Utd đ&uacute;ng ba điểm, đứng tr&ecirc;n cả hai CLB gi&agrave;u truyền thống l&agrave; Arsenal lẫn Liverpool. Sam Allardyce sẽ kh&ocirc;ng bao giờ được coi l&agrave; HLV ho&agrave;n hảo của West Ham, bởi đ&acirc;y l&agrave; CLB rất đề cao phong c&aacute;ch chơi b&oacute;ng. Nhưng chỉ cần &ocirc;ng gi&uacute;p đội b&oacute;ng đạt được th&agrave;nh t&iacute;ch tốt như những g&igrave; đ&atilde; thể hiện trong v&ograve;ng bốn th&aacute;ng qua ở giải Ngoại hạng Anh, s&acirc;n Upton Park chắc chắn sẽ im kh&ocirc;ng một tiếng phản đối. Cơ hội tồn tại: 8/10.</p>\r\n</div>\r\n\r\n<div class=\"post-source\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; text-align: right; font-weight: bold; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; line-height: 24px;\">Nguồn:&nbsp;<em>Nguyễn Ph&aacute;t - VnExpress</em></div>\r\n', '1', '0', '2014-12-21 23:51:24', null, '1', '0', '0', '0');
INSERT INTO `news` VALUES ('6', 'Từ Fabregas đến Wilshere và nỗi đau Wenger', '54997a536fd6a.jpg', '11', 'Từ Emirates đến Stamford Bridge, quãng đường chỉ dài chừng 13 km. Nhưng ở Emirates, người hâm mộ Arsenal đang rất tiếc nuối vì thấy đối thủ chỉ cách họ 15 phút lái xe hơi thi đấu thăng hoa nhờ một con người đã-từng-bị-chính-đội-bóng-chối-bỏ-hè -qua.', '<p><strong>Từ Emirates đến Stamford Bridge, qu&atilde;ng đường chỉ d&agrave;i chừng 13 km. Nhưng ở Emirates, người h&acirc;m mộ&nbsp;<a href=\"http://www.bongda.com.vn/tin-chinh/tu-fabregas-den-wilshere-va-noi-dau-wenger/www.bongda.com.vn/tags/Arsenal/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Arsenal&nbsp;</a>đang rất tiếc nuối v&igrave; thấy đối thủ chỉ c&aacute;ch họ 15 ph&uacute;t l&aacute;i xe hơi thi đấu thăng hoa nhờ một con người đ&atilde;-từng-bị-ch&iacute;nh-đội-b&oacute;ng-chối-bỏ-h&egrave; -qua.</strong></p>\r\n\r\n<p style=\"text-align: center;\"><strong><img alt=\"\" src=\"http://localhost:2024/uploads/pictures/news/jack2-620x330.jpg\" style=\"height:330px; width:620px\" /></strong></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family:tahoma,geneva,sans-serif; font-size:16px\">Arsene Wenger từng từ chối mua lại Cesc Fabregas</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">B</span>&aacute;o Daily Mail trước đ&ecirc;m Gi&aacute;ng sinh đưa ra con số g&acirc;y sốc với fan Arsenal. Thậm ch&iacute;, nhiều người sẽ kh&ocirc;ng tin v&agrave;o những g&igrave; tờ b&aacute;o Anh n&agrave;y đăng tải. Tuy nhi&ecirc;n, số liệu kh&ocirc;ng biết n&oacute;i dối v&agrave; fan của Ph&aacute;o thủ phải chấp nhận với thực tế.</p>\r\n\r\n<p style=\"text-align:justify\">Sau 16 trận ở Premier League trong m&agrave;u &aacute;o&nbsp;<a href=\"http://www.bongda.com.vn/tin-chinh/tu-fabregas-den-wilshere-va-noi-dau-wenger/www.bongda.com.vn/tags/chelsea/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Chelsea</a>, tiền vệ&nbsp;<a href=\"http://www.bongda.com.vn/tin-chinh/tu-fabregas-den-wilshere-va-noi-dau-wenger/www.bongda.com.vn/tags/Cesc-Fabregas%20/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Cesc Fabregas&nbsp;</a>c&oacute; 12 pha kiến tạo cho đồng đội lập c&ocirc;ng. Những g&igrave; cựu tiền vệ Arsenal l&agrave;m được c&ograve;n hơn cả 95 trận của tiền vệ Jack Wilshere đ&oacute;ng g&oacute;p cho đội chủ s&acirc;n Emirates, khi chỉ c&oacute; 11 đường kiến tạo.</p>\r\n\r\n<p style=\"text-align:justify\">C&ograve;n nhớ l&uacute;c ph&oacute;ng vi&ecirc;n hỏi HLV Arsene Wenger, liệu Fabregas c&oacute; một suất đ&aacute; ch&iacute;nh khi trở lại th&agrave;nh London hay kh&ocirc;ng; theo đ&oacute;, nh&agrave; cầm qu&acirc;n người Ph&aacute;p n&oacute;i: &ldquo;<em>Chắc chắn c&oacute;. Nhưng khi anh ấy rời đội b&oacute;ng, ch&uacute;ng t&ocirc;i đ&atilde; mua Mesut Oezil. Ngo&agrave;i ra, Arsenal c&ograve;n c&oacute; Santi Cazorla, Jack Wilshere v&agrave; kh&ocirc;ng cần th&ecirc;m cầu thủ tấn c&ocirc;ng n&agrave;o nữa</em>&ldquo;.</p>\r\n\r\n<p style=\"text-align:justify\">Nhưng sau v&ograve;ng 17, vị thế của Chelsea v&agrave; Arsenal ho&agrave;n to&agrave;n kh&aacute;c nhau. L&uacute;c n&agrave;y, ai c&ograve;n nhớ ph&aacute;t biểu của Wenger? Họ c&oacute; Mesut Oezil. Đ&uacute;ng. Nhưng ng&ocirc;i sao người Đức phải nghỉ đ&ocirc;ng sớm v&igrave; chấn thương. Trong khi đ&oacute;, Santi Cazorla v&agrave; Jack Wilshere c&oacute; phong độ thất thường.</p>\r\n\r\n<p style=\"text-align:justify\">Trong khi c&aacute;ch đ&oacute; 13 km, Cesc Fabregas đang c&ugrave;ng Chelsea bay cao. D&ugrave; anh mới c&oacute; 2 b&agrave;n tại Premier League, nhưng đi k&egrave;m đ&oacute; l&agrave; 12 pha kiến tạo chỉ sau 16 trận. Đ&aacute;ng n&oacute;i, đ&acirc;y l&agrave; năm đầu ti&ecirc;n Cesc Fabregas trở lại Premier League v&agrave; đ&aacute; cho Chelsea sau một thời gian &ldquo;đ&aacute;nh thu&ecirc;&rdquo; ở Barcelona.</p>\r\n\r\n<p style=\"text-align:justify\">Liệu c&oacute; c&ocirc;ng bằng khi so s&aacute;nh giữa Wilshere v&agrave; Fabregas? Nhưng HLV Arsene Wenger tin v&agrave;o c&aacute;i l&yacute; của m&igrave;nh. &Ocirc;ng từ chối cậu học tr&ograve; cũ v&agrave; trao cơ hội cho Wilshere. Song, từ đầu m&ugrave;a, tiền vệ người Anh mới ghi 1 b&agrave;n v&agrave; 1 pha kiến tạo trong 9 trận. C&ugrave;ng thời điểm, Fabregas c&oacute; 1 b&agrave;n v&agrave; 8 đường kiến tạo.</p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368506\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 630px;\"><a href=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/jack.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\"Jack Wilshere không để lại ấn tượng cho tới nay\" class=\"size-full wp-image-1368506\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/jack.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:620px\" /></a>\r\n\r\n<p>Jack Wilshere kh&ocirc;ng để lại ấn tượng cho tới nay</p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\">Đ&oacute; c&ograve;n chưa kể những g&igrave; Fabregas sắp thể hiện h&atilde;y c&ograve;n hứa hẹn rất nhiều so với Wilshere, bởi cầu thủ n&agrave;y giờ c&ograve;n đang phải l&agrave;m bạn với chấn thương. R&otilde; r&agrave;ng, những kiểu tin như b&aacute;o Daily Mail cung cấp cho độc giả chỉ c&oacute; thể l&agrave;m fan Chelsea h&agrave;i l&ograve;ng&hellip;</p>\r\n\r\n<p style=\"text-align:justify\">&hellip;c&ograve;n với Arsenal, họ tiếc nuối khi đội nh&agrave; để mất vi&ecirc;n ngọc qu&yacute;. Nếu c&oacute; Fabregas, mọi thứ giờ đ&atilde; s&aacute;ng sủa hơn với Ph&aacute;o thủ v&agrave; người ta sẽ kh&ocirc;ng phải nghe lời c&agrave;m r&agrave;m từ HLV Wenger. &ldquo;<em>Ch&uacute;ng t&ocirc;i chơi với 5 cầu thủ tấn c&ocirc;ng khi gặp Liverpool, nhưng vẫn thất bại trong việc tạo ra cơ hội</em>&ldquo;, Gi&aacute;o sư n&oacute;i sau trận ho&agrave; 2-2 với Liverpool cuối tuần qua.</p>\r\n', '1', '0', '2014-12-23 21:21:07', null, '1', null, null, '0');
INSERT INTO `news` VALUES ('7', 'Đội hình tiêu biểu vòng 17 Premier League: ‘Sát thủ’ Austin, Benteke và Silva được vinh danh', '54997aeb420ce.jpg', '11', 'Vòng 17 Premier League diễn ra với nhiều bàn thắng được ghi. Trong đó, nổi bật nhất là trận hòa 2-2 giữa Arsenal và Liverpool. Ở những diễn biến khác Manchester United bị cầm hòa, còn Manchester City vàChelsea tiếp tục cạnh tranh quyết liệt cho vị trí đầu bảng. Southampton cũng trở lại bằng chiến thắng ấn tượng trước Everton tuy nhiên họ không có một cầu thủ nào góp mặt trong đội hình tiêu biểu, cả Arsenal và M.U cũng thế.', '<p style=\"text-align:justify\"><strong>V&ograve;ng 17 Premier League diễn ra với nhiều b&agrave;n thắng được ghi. Trong đ&oacute;, nổi bật nhất l&agrave; trận h&ograve;a 2-2 giữa&nbsp;<a href=\"http://beta.bongda.com.vn/tags/arsenal\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Arsenal</a>&nbsp;v&agrave; Liverpool. Ở những diễn biến kh&aacute;c Manchester United bị cầm h&ograve;a, c&ograve;n Manchester City v&agrave;<a href=\"http://beta.bongda.com.vn/tags/Chelsea\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Chelsea</a>&nbsp;tiếp tục cạnh tranh quyết liệt cho vị tr&iacute; đầu bảng. Southampton cũng trở lại bằng chiến thắng ấn tượng trước Everton tuy nhi&ecirc;n họ kh&ocirc;ng c&oacute; một cầu thủ n&agrave;o g&oacute;p mặt trong đội h&igrave;nh ti&ecirc;u biểu, cả Arsenal v&agrave; M.U cũng thế.</strong></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368457\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 630px;\"><a href=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/austin1.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\"Austin chơi rất hay trong trận gặp West Brom\" class=\"size-full wp-image-1368457\" src=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/austin1.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:620px\" /></a>\r\n\r\n<p>Austin chơi rất hay trong trận gặp West Brom</p>\r\n</div>\r\n\r\n<p><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">&nbsp;H</span><span style=\"font-family:tahoma,geneva,sans-serif; font-size:16px\">&atilde;y c&ugrave;ng ch&uacute;ng t&ocirc;i điểm qua đội h&igrave;nh ti&ecirc;u biểu v&ograve;ng 17 Premier League theo b&igrave;nh chọn của Bleacherreport.com. Dưới đ&acirc;y l&agrave; những cầu thủ ph&ugrave; hợp với sơ đồ 4-2-2-2.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Thủ m&ocirc;n:&nbsp;<strong>Costel Pantilimon</strong>&nbsp;(Sunderland)</p>\r\n\r\n<p style=\"text-align:justify\">Th&ecirc;m một lần nữa Pantilimon c&oacute; m&agrave;n tr&igrave;nh diễn kh&ocirc;ng thể ch&ecirc; v&agrave;o đ&acirc;u được trong m&ugrave;a giải n&agrave;y. Những pha cứu thua xuất sắc của anh gi&uacute;p &ldquo;M&egrave;o đen&rdquo; bảo to&agrave;n một chiến thắng trước Newcastle ngay tr&ecirc;n s&acirc;n kh&aacute;ch. Với phong độ hiện tại của Pantilimon, sẽ rất kh&oacute; để Vito Mannone đ&ograve;i lại vị tr&iacute; ch&iacute;nh thức đ&atilde; sở hữu ở m&ugrave;a trước.</p>\r\n\r\n<p style=\"text-align:justify\">Hậu vệ phải:<strong>&nbsp;Pablo Zabaleta</strong>&nbsp;(Manchester City)</p>\r\n\r\n<p style=\"text-align:justify\">Trong trận đấu với Crystal Palace, Zabaleta l&agrave; mối đe dọa thực sự đối với đội kh&aacute;ch b&ecirc;n c&aacute;nh phải. Anh đ&aacute;nh dấu một ng&agrave;y thi đấu ấn tượng bằng pha kiến tạo cho tiền vệ David Silva ghi b&agrave;n mở tỷ số, đ&oacute; l&agrave; b&agrave;n thắng khai th&ocirc;ng thế bế tắc từ đ&oacute; Man City c&oacute; chiến thắng đậm.</p>\r\n\r\n<p style=\"text-align:justify\">Trung vệ:<strong>&nbsp;Ron Vlaar</strong>&nbsp;(Aston Villa)</p>\r\n\r\n<p style=\"text-align:justify\">Mục ti&ecirc;u săn đuổi của M.U được b&igrave;nh luận vi&ecirc;n Iain Macintosh của ESPNFC v&iacute; l&agrave; một cầu thủ &ldquo;vượt ra khỏi Th&aacute;i B&igrave;nh Dương&rdquo; sau khi chứng kiến anh kh&oacute;a chặt nhiều ng&ograve;i nổ của đội kh&aacute;ch, cụ thể l&agrave; Radamel Falcao. Vlaar c&ugrave;ng với Ciaran Clark v&agrave; Jores Okore hợp th&agrave;nh bộ 3 hậu vệ trong sơ đồ 3-5-2 của Aston Villa. R&otilde; r&agrave;ng, nếu HLV Louis van Gaal muốn &aacute;p dụng sơ đồ 3-5-2 cho &ldquo;Quỷ đỏ&rdquo; th&igrave; &ocirc;ng n&ecirc;n chi&ecirc;u mộ Vlaar.</p>\r\n\r\n<p style=\"text-align:justify\">Trung vệ:&nbsp;<strong>John Terry</strong>&nbsp;(Chelsea)</p>\r\n\r\n<p style=\"text-align:justify\">Chelsea c&oacute; chuyến l&agrave;m kh&aacute;ch cực kỳ kh&oacute; khăn tr&ecirc;n s&acirc;n của Stoke City v&agrave;&nbsp;<a href=\"http://www.bongda.com.vn/tin-chinh/vuot-ai-britannia-chelsea-xay-chac-ngoi-dau/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\" title=\"Vượt ải Britannia, Chelsea xây chắc ngôi đầu\">họ đ&atilde; ghi b&agrave;n trước nhờ c&ocirc;ng của Terry ngay ph&uacute;t thứ 2</a>. Sau đ&oacute;, The Blues kh&eacute;p lại 90 ph&uacute;t bằng một chiến thắng, Terry kh&ocirc;ng chỉ ghi b&agrave;n m&agrave; c&ograve;n l&agrave;m rất tốt nhiệm vụ ph&ograve;ng ngự của m&igrave;nh.</p>\r\n\r\n<p style=\"text-align:justify\">Hậu vệ tr&aacute;i:&nbsp;<strong>Lazar Markovic</strong>&nbsp;(Liverpool)</p>\r\n\r\n<p style=\"text-align:justify\">Ch&uacute;ng ta kh&ocirc;ng nghĩ rằng Markovic đủ sức c&oacute; mặt trong đội h&igrave;nh ti&ecirc;u biểu nhưng điều n&agrave;y đ&atilde; xảy ra. Ở một vị tr&iacute; m&agrave; CĐV Liverpool lạ lẫm với cầu thủ người Serbia, Markovic xứng đ&aacute;ng nhận được lời khen ngợi. Hồi giữa tuần trước, anh cũng chơi tốt trong trận thắng Bournemouth ở League Cup.</p>\r\n\r\n<p style=\"text-align:justify\">Tiền vệ trung t&acirc;m:&nbsp;<strong>Nemanja Matic</strong>&nbsp;(Chelsea)</p>\r\n\r\n<p style=\"text-align:justify\">Matic được b&igrave;nh chọn l&agrave; cầu thủ xuất sắc nhất trong trận đấu giữa Chelsea v&agrave; Stoke City. Tiền vệ người Serbia chứng tỏ vai tr&ograve; cốt l&otilde;i của m&igrave;nh trong đội h&igrave;nh của The Blues bằng những pha đ&aacute;nh chặn ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p style=\"text-align:justify\">Tiền vệ trung t&acirc;m:<strong>&nbsp;Stewart Downing</strong>&nbsp;(West Ham)</p>\r\n\r\n<p style=\"text-align:justify\">Downing đang c&oacute; phong độ rất cao trong m&agrave;u &aacute;o West Ham. B&agrave;n thắng của tiền vệ n&agrave;y trước Leicester City kh&ocirc;ng chỉ khẳng định chiến thắng cho West Ham m&agrave; c&ograve;n gi&uacute;p đội nh&agrave; giữ vững vị tr&iacute; thứ tư tr&ecirc;n bảng xếp hạng, hơn Southampton 2 điểm.</p>\r\n\r\n<p style=\"text-align:justify\">Tiền vệ c&ocirc;ng:&nbsp;<strong>Philippe Coutinho</strong>&nbsp;(Liverpool)</p>\r\n\r\n<p style=\"text-align:justify\">Tiền vệ người Brazil kh&ocirc;ng thể hiện được nhiều trong phần lớn thời gian của giai đoạn lượt đi. Tuy nhi&ecirc;n, trong 3 hoặc 4 trận đấu gần đ&acirc;y anh chơi kh&aacute; hay. Coutinho l&agrave; người khai th&ocirc;ng thế bế tắc cho Liverpool trước Arsenal, mặc d&ugrave; sau đ&oacute; họ bị đội kh&aacute;ch vươn l&ecirc;n dẫn trước v&agrave; vất vả t&igrave;m một kết quả h&ograve;a ở ph&uacute;t thứ 97.</p>\r\n\r\n<p style=\"text-align:justify\">Tiền vệ c&ocirc;ng:&nbsp;<strong>David Silva</strong>&nbsp;(Manchester City)</p>\r\n\r\n<p style=\"text-align:justify\">Qu&aacute; ấn tượng! Đ&oacute; l&agrave; những g&igrave; m&agrave; CĐV The Citizens phải thốt l&ecirc;n khi chứng kiến tiền vệ người T&acirc;y Ban Nha lập c&uacute; đ&uacute;p v&agrave;o lưới Crystal Palace. Ở trận n&agrave;y, Silva được bố tr&iacute; chơi trung t&acirc;m h&agrave;ng tiền vệ v&agrave; anh tỏ ra rất nhạy b&eacute;n.</p>\r\n\r\n<p style=\"text-align:justify\">Tiền đạo:&nbsp;<strong>Christian Benteke</strong>&nbsp;(Aston Villa)</p>\r\n\r\n<p style=\"text-align:justify\">Jonny Evans l&agrave; người được HLV Louis van Gaal giao nhiệm vụ k&egrave;m chặt Benteke nhưng trong suốt 90 ph&uacute;t cầu thủ n&agrave;y kh&ocirc;ng ho&agrave;n th&agrave;nh nhiệm vụ. Benteke cho thấy những phẩm chất của một s&aacute;t thủ bằng b&agrave;n thắng mở tỷ số trận đấu v&agrave; nhiều lần uy hiếp khung th&agrave;nh David de Gea. Chỉ tiếc, Aston Villa kh&ocirc;ng thể giữ vững lợi thế trước sức &eacute;p lớn từ h&agrave;ng c&ocirc;ng của M.U.</p>\r\n\r\n<p style=\"text-align:justify\">Tiền đạo:&nbsp;<strong>Charlie Austin</strong>&nbsp;(Queens Park Rangers)</p>\r\n\r\n<p style=\"text-align:justify\">Sẽ rất kh&oacute; để b&igrave;nh chọn ai l&agrave; cầu thủ chơi ấn tượng nhất v&ograve;ng 17 n&agrave;y, nhưng nếu chỉ g&oacute;i gọn trong số c&aacute;c tiền đạo th&igrave; c&oacute; lẽ Austin l&agrave; người xuất sắc nhất. Anh lập một hattrick gi&uacute;p QPR lội ngược d&ograve;ng ngoạn mục trước West Brom sau khi bị dẫn trước 2-0 nửa đầu hiệp 1. Austin cũng n&acirc;ng cao th&agrave;nh t&iacute;ch ghi b&agrave;n của m&igrave;nh trong m&ugrave;a giải n&agrave;y khi anh đ&atilde; c&oacute; 11 b&agrave;n thắng qua 15 trận đấu tr&ecirc;n mọi đấu trường.</p>\r\n', '1', '0', '2014-12-23 21:23:39', null, null, null, null, '0');
INSERT INTO `news` VALUES ('8', 'Công Phượng và đồng đội ‘nhức nhối’ chứng kiến người Thái ăn mừng AFF Cup', '54997b7f81f8f.jpg', '10', 'Có mặt tại Thái Lan vào thời điểm người hâm mộ nước này đang ăn mừng chiến thắng của tuyển quốc gia tại AFF Cup 2014, Công Phượng và các thành viên của CLB Hoàng Anh Gia Lai (HAGL) dâng lên rất nhiều cảm xúc.', '<p style=\"text-align:justify\"><strong>C&oacute; mặt tại Th&aacute;i Lan v&agrave;o thời điểm&nbsp;<a href=\"http://www.bongda.com.vn/aff-cup/tuyen-thai-lan-duoc-don-tiep-nong-nhiet-khi-tro-ve/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">người h&acirc;m mộ nước n&agrave;y đang ăn mừng chiến thắng của tuyển quốc gia tại AFF Cup 2014</a>,&nbsp;<a href=\"http://bongda.com.vn/tags/Nguyen-Cong-Phuong/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">C&ocirc;ng Phượng</a>&nbsp;v&agrave; c&aacute;c th&agrave;nh vi&ecirc;n của CLB&nbsp;<a href=\"http://bongda.com.vn/tags/Hoang-Anh-Gia-Lai/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Ho&agrave;ng Anh Gia Lai</a>&nbsp;(HAGL) d&acirc;ng l&ecirc;n rất nhiều cảm x&uacute;c.</strong></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Công Phượng và đồng đội ‘nhức nhối’ chứng kiến người Thái ăn mừng AFF Cup\" class=\"aligncenter size-full wp-image-1368478\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/HAGL20.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; clear:both; display:block; height:auto; list-style:none; margin:2px auto; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:600px\" /></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368479\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 610px;\"><img alt=\"Công Phượng và đồng đội ‘nhức nhối’ chứng kiến người Thái ăn mừng AFF Cup\" class=\"size-full wp-image-1368479\" src=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/HAGL111.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:600px\" />\r\n<p>Tuyển Th&aacute;i Lan diễu h&agrave;nh b&aacute;o c&ocirc;ng sau chiến thắng tại AFF Cup 2014</p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">H</span>&igrave;nh ảnh n&agrave;y, hy vọng sẽ l&agrave; động lực mạnh mẽ cho c&aacute;c cầu thủ HAGL Tuyển Th&aacute;i Lan diễu h&agrave;nh b&aacute;o c&ocirc;ng sau chiến thắng tại AFF Cup 2014</p>\r\n\r\n<p style=\"text-align:justify\">Tr&ecirc;n trang c&aacute; nh&acirc;n của m&igrave;nh, tiền đạo C&ocirc;ng Phượng cho rằng việc người Th&aacute;i&nbsp;<em>&ldquo;suốt ng&agrave;y v&ocirc; địch Đ&ocirc;ng Nam &Aacute;&rdquo;</em>&nbsp;l&agrave; nhờ v&agrave;o t&iacute;nh chuy&ecirc;n nghiệp trong tổ chức ở cấp c&acirc;u lạc bộ, ph&acirc;n c&ocirc;ng lao động rất rạch r&ograve;i, tỉ mỉ. Ngo&agrave;i ra l&agrave; điều kiện s&acirc;n b&atilde;i miễn ch&ecirc; c&ugrave;ng tinh thần fair-play trong thi đấu, điển h&igrave;nh từ trận giao hữu với PTT Rayong nơi HAGL thắng 3-2.</p>\r\n\r\n<p style=\"text-align:justify\"><em>&ldquo;Điều m&agrave; gi&uacute;p B&oacute;ng đ&aacute; Th&aacute;i Lan th&agrave;nh c&ocirc;ng l&agrave; lối đ&aacute; của họ. Họ đ&aacute; b&oacute;ng chứ kh&ocirc;ng phải đ&aacute; người, kh&ocirc;ng bao giờ v&agrave;o b&oacute;ng &aacute;c &yacute;, hay d&ugrave;ng những tiểu xảo, khiến cho cả 2 b&ecirc;n chơi b&oacute;ng một c&aacute;ch thoải m&aacute;i v&agrave; cảm thấy rất an to&agrave;n kh&ocirc;ng phải đ&aacute; trong cảm gi&aacute;c lo sợ.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>V&agrave; đ&oacute; mới chỉ l&agrave; CLB hạng b&eacute;t của Th&aacute;i Lan, thử hỏi một CLB lớn nhất Việt Nam đ&atilde; l&agrave;m được như vậy chưa?&rdquo;</em>, C&ocirc;ng Phượng viết.</p>\r\n\r\n<p style=\"text-align:justify\">Tại Bangkok, sau khi nh&igrave;n thấy xe bu&yacute;t 2 tầng chở tuyển Th&aacute;i Lan ăn mừng c&ugrave;ng h&agrave;ng ng&agrave;n fan đi &ldquo;b&atilde;o&rdquo; trước mặt, C&ocirc;ng Phượng trầm mặc hẳn.</p>\r\n\r\n<p style=\"text-align:justify\">Đi c&ugrave;ng anh, những đồng đội Tuấn Anh, Xu&acirc;n Trường, Văn Sơn, Hồng Duy, Đ&ocirc;ng Triều&hellip; b&igrave;nh thường rất &ldquo;t&iacute;a lia&rdquo; cũng đều &iacute;t n&oacute;i hơn. Chứng kiến cảnh đ&oacute;, trợ l&yacute; ng&ocirc;n ngữ Nguyễn Hữu Tr&iacute; m&ocirc; tả bằng status:&nbsp;<em>&ldquo;L&ograve;ng v&ograve;ng cuối ng&agrave;y lại gặp cảnh n&agrave;y, buồn c&agrave;ng th&ecirc;m rầu&rdquo;.</em></p>\r\n\r\n<p style=\"text-align:justify\">D&ugrave; sau đ&oacute; đ&atilde; đ&oacute;ng c&aacute;c t&agrave;i khoản mạng x&atilde; hội để tr&aacute;nh bị ph&acirc;n t&acirc;m, nhưng nhận định của C&ocirc;ng Phượng phần n&agrave;o n&oacute;i l&ecirc;n kh&aacute;t khao chiến thắng của ch&agrave;ng trai trẻ khi&hellip; buộc phải l&agrave;m nh&acirc;n chứng cho ng&agrave;y hội mừng c&ocirc;ng của &ldquo;k&igrave;nh địch&rdquo; b&oacute;ng đ&aacute; Việt.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Công Phượng và đồng đội ‘nhức nhối’ chứng kiến người Thái ăn mừng AFF Cup\" class=\"aligncenter size-full wp-image-1368480\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/HAGL23.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; clear:both; display:block; height:auto; list-style:none; margin:2px auto; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:600px\" /></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368481\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 610px;\"><img alt=\"Công Phượng và đồng đội ‘nhức nhối’ chứng kiến người Thái ăn mừng AFF Cup\" class=\"size-full wp-image-1368481\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/HAGL32.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:600px\" />\r\n<p>H&igrave;nh ảnh trầm mặc của c&aacute;c cầu thủ HAGL khi thấy Th&aacute;i Lan mở hội</p>\r\n</div>\r\n', '1', '0', '2014-12-23 21:26:07', null, null, null, null, '0');
INSERT INTO `news` VALUES ('9', 'Cesc Fabregas: Chiếc đũa thần của The Blues', '54997c0b362e7.jpg', '11', 'Trước áp lực khủng khiếp từ phía Man City sau khi nhà ĐKVĐ nước Anh cân bằng được điểm số cộng thêm việc phải làm khách trên sân Britania của một Stoke khó chơi có nhiều dự đoán cho rằng Chelsea sẽ khó đứng vững. ', '<p style=\"text-align:justify\"><strong>Trước &aacute;p lực khủng khiếp từ ph&iacute;a&nbsp;<a href=\"http://bongda.com.vn/tags/Manchester-City/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Man City</a>&nbsp;sau khi nh&agrave; ĐKVĐ nước Anh c&acirc;n bằng được điểm số cộng th&ecirc;m việc&nbsp;<a href=\"http://www.bongda.com.vn/tin-chinh/vuot-ai-britannia-chelsea-xay-chac-ngoi-dau/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\" title=\"Vượt ải Britannia, Chelsea xây chắc ngôi đầu\">phải l&agrave;m kh&aacute;ch tr&ecirc;n s&acirc;n Britania của một Stoke kh&oacute; chơi</a>&nbsp;c&oacute; nhiều dự đo&aacute;n cho rằng&nbsp;<a href=\"http://bongda.com.vn/tags/Chelsea/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Chelsea</a>&nbsp;sẽ kh&oacute; đứng vững. Thực tế, mọi chuyện cũng kh&ocirc;ng phải dễ d&agrave;ng với The Blues nhưng&nbsp;<a href=\"http://bongda.com.vn/tags/Cesc-Fabregas/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Cesc Fabregas</a>&nbsp;ch&iacute;nh l&agrave; điều kh&aacute;c biệt.</strong></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368445\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 660px;\"><img alt=\"Cesc Fabregas chính là sự khác biệt trong trận đấu vừa qua. Ảnh internet\" class=\"size-full wp-image-1368445\" src=\"http://a7.img.bongda.com.vn/wp-content/uploads/2014/12/Berharap.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:650px\" />\r\n<p>Cesc Fabregas ch&iacute;nh l&agrave; sự kh&aacute;c biệt trong trận đấu vừa qua. Ảnh internet</p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">T</span>rước một Stoke City m&aacute;u lửa v&agrave; sẵn s&agrave;ng chơi rắn, Jose Mourinho vẫn phải tung hết những con b&agrave;i quan trọng nhất v&agrave;o s&acirc;n m&agrave; kh&ocirc;ng c&oacute; sự xoay tua n&agrave;o bởi Người đặc biệt nhận thức được rằng chỉ 1 lần sảy ch&acirc;n nữa họ c&oacute; thể bị đ&aacute; văng khỏi ng&ocirc;i đầu. Chức v&ocirc; địch m&ugrave;a Đ&ocirc;ng đ&atilde; ở rất gần mặc d&ugrave; n&oacute; kh&ocirc;ng ch&iacute;nh thức nhưng l&agrave; sự kh&iacute;ch lệ lớn lao cho cả chặng đường c&ograve;n lại của m&ugrave;a giải.</p>\r\n\r\n<p style=\"text-align:justify\">Diego Costa vẫn c&oacute; mặt ở tr&ecirc;n s&acirc;n nhưng ho&agrave;n to&agrave;n tịt ng&ograve;i, thậm ch&iacute; tiền đạo người T&acirc;y Ban Nha c&ograve;n c&oacute; kh&ocirc;ng dưới 2 cơ hội ngon ăn nhưng kh&ocirc;ng thể tận dụng. Rất may Chelsea c&ograve;n c&oacute; 1 Fabregas si&ecirc;u đẳng ở ph&iacute;a sau, nếu kh&ocirc;ng Chelsea sẽ c&ograve;n mướt mồ h&ocirc;i mới gi&agrave;nh được 3 điểm tr&ecirc;n s&acirc;n Britania.</p>\r\n\r\n<p style=\"text-align:justify\">Ở trận n&agrave;y,&nbsp;Cesc Fabregas được đ&aacute; cao hơn như một hộ c&ocirc;ng thay cho Oscar, vị tr&iacute; n&agrave;y c&agrave;ng l&agrave;m cho Fabregas th&ecirc;m nguy hiểm bởi sự thoải m&aacute;i kh&ocirc;ng phải qu&aacute; lo ph&ograve;ng ngự khi đ&atilde; c&oacute; 2 m&aacute;y qu&eacute;t Matic v&agrave; Mikel ở ph&iacute;a sau. Ngo&agrave;i b&agrave;n thắng c&ugrave;ng đường kiến tạo th&agrave;nh b&agrave;n, Fabregas cũng c&oacute; nhiều lần l&agrave;m x&eacute; toang h&agrave;ng ph&ograve;ng ngự rắn chắc b&ecirc;n ph&iacute;a đội chủ nh&agrave; thế nhưng c&aacute;c đồng đội đ&atilde; kh&ocirc;ng tận dụng hết được.</p>\r\n\r\n<p style=\"text-align:justify\">Hồi đầu m&ugrave;a, khi Diego Costa ghi b&agrave;n s&ograve;n s&ograve;n,&nbsp;Cesc Fabregas đ&atilde; bị c&aacute;i b&oacute;ng của cựu tiền đạo Atletico Madrid che lấp, nhưng khi Costa gặp kh&oacute; ch&iacute;nh tiền vệ 27 tuổi đ&atilde; kh&ocirc;ng &iacute;t lần giải nguy cho Chelsea. Rất hiếm trận n&agrave;o m&agrave; c&aacute;c b&agrave;n thắng lại kh&ocirc;ng c&oacute; dấu gi&agrave;y của cựu tiền vệ Barcelona. Thậm ch&iacute; khi Chelsea c&oacute; b&agrave;n thắng, người ta nghĩ ngay đến việc Fabregas l&agrave; người kiến tạo. Chỉ sau 21 trận m&ugrave;a n&agrave;y, Fabregas đ&atilde; c&oacute; tới 15 đường chuyền th&agrave;nh b&agrave;n cộng với 4 b&agrave;n thắng, một con số ho&agrave;n hảo.</p>\r\n\r\n<p style=\"text-align:justify\">Chelsea đ&atilde; thay đổi lớn lao so với m&ugrave;a trước, khi m&agrave; họ đ&atilde; thực sự lạnh l&ugrave;ng v&agrave; kh&ocirc;ng c&ograve;n ngu ngơ trước c&aacute;c đối thủ yếu. Diego Costa l&agrave; người đ&aacute; cao nhất tr&ecirc;n h&agrave;ng c&ocirc;ng nhưng phải thực sự thừa nhận rằng Fabregas mới l&agrave; người l&agrave;m thay da đổi thịt, người thổi hồn v&agrave;o lối chơi l&agrave;m đo&agrave;n qu&acirc;n của Jose Mourinho bay bổng v&agrave; bớt cục mịch hơn. Tuy nhi&ecirc;n, Fabregas vẫn giữ được sự chắc chắn v&agrave; đơn giản cần thiết, v&igrave; lẽ đ&oacute; Người đặc biệt kh&ocirc;ng c&ograve;n g&igrave; để ph&agrave;n n&agrave;n về tiền vệ n&agrave;y.</p>\r\n\r\n<p style=\"text-align:justify\">Khi&nbsp;Cesc Fabregas gia nhập Stamford Bridge, Arsene Wenger đ&atilde; l&ecirc;n tiếng rằng Arsenal kh&ocirc;ng cần sự phục vụ của tiền vệ người T&acirc;y Ban Nha bởi họ đ&atilde; c&oacute; những người tiếp đạn xuất sắc kh&aacute;c đặc biệt l&agrave; Mesut Oezil. Nhưng hiện tại trong khi Oezil đang ch&igrave;m nghỉm trong chấn thương th&igrave; Fabregas lại thăng hoa như một v&igrave; tinh t&uacute;. C&oacute; lẽ Gi&aacute;o sư đ&atilde; qu&ecirc;n mất những ng&agrave;y tiền vệ người T&acirc;y Ban Nha c&ograve;n l&agrave; nhạc trưởng của Ph&aacute;o thủ l&agrave;m đi&ecirc;u đứng cả Premier League chăng.</p>\r\n\r\n<p style=\"text-align:justify\">Barcelona cũng n&ecirc;n hối tiếc bởi họ đ&atilde; đ&aacute;nh mất một tiền vệ kiến thiết si&ecirc;u việt, một c&acirc;y đũa thần c&oacute; thể tạo ra những ph&eacute;p m&agrave;u quyết định trận đấu. Premier League ch&iacute;nh l&agrave; nơi đưa t&ecirc;n tuổi Fabregas l&ecirc;n tầm thế giới rồi cũng ch&iacute;nh l&agrave; nơi l&agrave;m hồi sinh lại sau những khoảng thời gian trầm lắng của tiền vệ n&agrave;y khi hồi hương T&acirc;y Ban Nha. Fabregas vẫn ở London chỉ kh&aacute;c rằng anh kh&ocirc;ng c&ograve;n l&agrave; một Ph&aacute;o thủ m&agrave; đ&atilde; kho&aacute;c l&ecirc;n m&igrave;nh chiếc &aacute;o The Blues. Chelsea c&oacute; lẽ đ&atilde; n&ecirc;n hạnh ph&uacute;c v&igrave; sở hữu được Cesc Fabregas, c&ograve;n Arsenal họ chỉ c&ograve;n biết tự tr&aacute;ch m&igrave;nh m&agrave; th&ocirc;i.</p>\r\n', '1', '1', '2014-12-23 21:28:27', null, '5', '0', '0', '0');
INSERT INTO `news` VALUES ('10', 'Lộ cái tên rời Man United trong mùa đông', '54997caa68a80.jpg', '5', 'Theo Daily Mail, Man United sẽ chia tay Adnan Januzaj trong mùa đông này, tất nhiên là dưới bản hợp đồng cho mượn.', '<p style=\"text-align:justify\"><strong>Theo Daily Mail,&nbsp;<a href=\"http://beta.bongda.com.vn/tags/manchester-united\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Man United&nbsp;</a>sẽ chia tay<a href=\"http://beta.bongda.com.vn/tags/adnan-januzaj\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">&nbsp;Adnan Januzaj</a>trong m&ugrave;a đ&ocirc;ng n&agrave;y, tất nhi&ecirc;n l&agrave; dưới bản hợp đồng cho mượn.</strong></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1339087\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 630px;\"><a href=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/11/januzaokwp.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\" Man United sẽ chia tay Adnan Januzaj trong mùa đông\" class=\"size-full wp-image-1339087\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/11/januzaokwp.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:620px\" /></a>\r\n\r\n<p>Man United sẽ chia tay Adnan Januzaj trong m&ugrave;a đ&ocirc;ng</p>\r\n</div>\r\n\r\n<p><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">A</span><span style=\"font-family:tahoma,geneva,sans-serif; font-size:16px\">dnan Januzaj năm nay chỉ mới 19 tuổi, l&agrave; điểm s&aacute;ng của Man United m&ugrave;a trước. Tiền vệ người Bỉ được đ&aacute;nh gi&aacute; cao ở tốc độ, kĩ thuật c&ugrave;ng sự tự tin.</span></p>\r\n\r\n<p>Sau khi Giggs gi&atilde; từ sự nghiệp thi đấu, Januzaj được trao lại chiếc &aacute;o số 11 huyền thoại. Tuy nhi&ecirc;n, t&iacute;nh đến thời điểm n&agrave;y, anh chưa thể đ&aacute;p ứng lại sự k&igrave; vọng. Con số 11 lần ra s&acirc;n (7 dự bị), 0 b&agrave;n thắng, 0 đường kiến tạo của Januzaj thực sự ngh&egrave;o n&agrave;n.</p>\r\n\r\n<p style=\"text-align:justify\">Januzaj kh&ocirc;ng mạnh mẽ, cần mẫn như Valencia, Young, cũng kh&ocirc;ng xuất sắc như Angel di Maria, Mata. Bởi thế, anh kh&ocirc;ng được tin d&ugrave;ng cũng l&agrave; điều dễ hiểu.</p>\r\n\r\n<p style=\"text-align:justify\">D&ugrave; sao, HLV Louis van Gaal vẫn nhận thấy được tiềm năng của Januzaj n&ecirc;n quyết định đem anh cho mượn ở một CLB của Premier League trong th&aacute;ng Gi&ecirc;ng n&agrave;y. Man United cũng đ&atilde; nhận được kh&ocirc;ng &iacute;t lời đề nghị hấp dẫn cho Januzaj.</p>\r\n', '1', '0', '2014-12-23 21:31:06', null, '1', null, null, '0');
INSERT INTO `news` VALUES ('11', 'Cựu tiền đạo Bayern Munich, Man City chia tay Malaga', '54997cff57bed.jpg', '5', 'Tiền đạo kì cựu Roque Santa Cruz đang trên đường rời Malaga để hoàn tất thương vụ gia nhập Cruz Azul.', '<p style=\"text-align:justify\"><strong>Tiền đạo k&igrave; cựu Roque Santa Cruz đang tr&ecirc;n đường rời Malaga để ho&agrave;n tất thương vụ gia nhập Cruz Azul.</strong></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368450\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 610px;\"><a href=\"http://a7.img.bongda.com.vn/wp-content/uploads/2014/12/santaSSS.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\"Santa Cruz chia tay bóng đá châu Âu sau 15 năm chơi bóng\" class=\"size-full wp-image-1368450\" src=\"http://a7.img.bongda.com.vn/wp-content/uploads/2014/12/santaSSS.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:600px\" /></a>\r\n\r\n<p>Santa Cruz chia tay b&oacute;ng đ&aacute; ch&acirc;u &Acirc;u sau 15 năm chơi b&oacute;ng</p>\r\n</div>\r\n\r\n<p><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">T</span><span style=\"font-family:tahoma,geneva,sans-serif; font-size:16px\">rận đấu cuối tuần qua khi Malaga thắng Elche c&oacute; thể l&agrave; trận đấu cuối c&ugrave;ng của tiền đạo 33 tuổi Santa Cruz cho đội b&oacute;ng T&acirc;y Ban Nha. Ch&acirc;n s&uacute;t người Paraguay sau 15 năm chinh chiến tại ch&acirc;u &Acirc;u tr&ecirc;n đường về lại ch&acirc;u Mỹ dưỡng gi&agrave;.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Malaga được cho sẽ nhận 500 ng&agrave;n Euro ph&iacute; chuyển nhượng, c&ograve;n Santa Cruz sẽ trở th&agrave;nh ng&ocirc;i sao được trả lương cao nhất Mexico l&ecirc;n đến 2 triệu Dollar/ năm (1,6 triệu Euro), cao hơn Ronaldinho 300 ng&agrave;n Euro.</p>\r\n\r\n<p style=\"text-align:justify\">Trong qu&aacute; khứ của m&igrave;nh, Santa Cruz l&agrave; cầu thủ danh tiếng bậc nhất của b&oacute;ng đ&aacute; Paraguay, chơi b&oacute;ng cho Bayern Muncich khi c&ograve;n rất trẻ (năm 1999). Sau đ&oacute;, Santa Cruz sang Anh chơi b&oacute;ng cho Blackburn, Man City.</p>\r\n\r\n<p style=\"text-align:justify\">Khi qua sườn dốc sự nghiệp, Santa Cruz sang T&acirc;y Ban Nha chơi b&oacute;ng trong 2 năm rưỡi cho Malaga. K&igrave; CN th&aacute;ng Một n&agrave;y, tiền đạo k&igrave; cựu quen mặt thuộc t&ecirc;n tại ch&acirc;u &Acirc;u t&iacute;nh sang giải nh&agrave; nghề Mỹ MLS nhưng cuối c&ugrave;ng chọn sang Mexico chơi cho Cruz Azul.</p>\r\n', '1', '0', '2014-12-23 21:32:31', null, null, null, null, '0');
INSERT INTO `news` VALUES ('12', 'VFF sẽ bừng tỉnh sau phát biểu của ông Miura?', '54997d984758e.jpg', '10', 'Phát biểu của HLV Toshiya Miura về những góc khuất của Bóng đá Việt Nam vừa qua đã khiến không ít lãnh đạo Liên đoàn Bóng đá Việt Nam (VFF) phải giật mình.', '<div><span style=\"font-size:14px\"><strong>Ph&aacute;t biểu của HLV Toshiya Miura về những g&oacute;c khuất của B&oacute;ng đ&aacute; Việt Nam vừa qua&nbsp;đ&atilde; khiến kh&ocirc;ng &iacute;t l&atilde;nh đạo Li&ecirc;n đo&agrave;n B&oacute;ng đ&aacute; Việt Nam (VFF) phải giật m&igrave;nh.</strong></span></div>\r\n\r\n<div class=\"entry\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; line-height: 1.5; text-align: justify; font-family: Tahoma, Geneva, sans-serif; font-size: 16px !important;\">\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368383\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; width: 675px;\"><img alt=\"VFF sẽ bừng tỉnh sau phát biểu của ông Miura?\" class=\"size-full wp-image-1368383\" src=\"http://a7.img.bongda.com.vn/wp-content/uploads/2014/12/Toshiya2.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:665px\" />\r\n<p>HLV Toshiya Miura (tr&aacute;i) v&agrave; Ph&oacute; chủ tịch VFF Trần Quốc Tuấn &ndash; Ảnh: Khả H&ograve;a</p>\r\n</div>\r\n\r\n<p><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">T</span>rước giờ cũng c&oacute; một v&agrave;i HLV n&oacute;i về c&aacute;ch điều h&agrave;nh của VFF, như kiểu &ocirc;ng Alfred Riedl thẳng thắn:&nbsp;<em>&ldquo;<a href=\"http://www.bongda.com.vn/cat/viet-nam/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">B&oacute;ng đ&aacute; Việt Nam&nbsp;</a>x&acirc;y nh&agrave; từ n&oacute;c&rdquo;.</em>&nbsp;Nhưng những ph&aacute;t ng&ocirc;n của c&aacute;c vị HLV trước đ&acirc;y thường ph&aacute;t ra trong ho&agrave;n cảnh họ bị VFF sa thải, thanh l&yacute; hợp đồng, chứ kh&ocirc;ng như &ocirc;ng&nbsp;<a href=\"http://bongda.com.vn/tags/Toshiya-Miura/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Miura</a>.</p>\r\n\r\n<p>Ở thời điểm n&agrave;y, &ocirc;ng Miura đang được VFF tin cậy v&agrave; tạo mọi điều kiện tốt nhất, ch&iacute;nh v&igrave; thế, ph&aacute;t ng&ocirc;n của nh&agrave; cầm qu&acirc;n người Nhật cho thấy &ocirc;ng l&agrave; người thẳng thắn, n&oacute;i thẳng n&oacute;i thật, chứ kh&ocirc;ng uốn m&igrave;nh để được thuận lợi trong c&ocirc;ng việc.</p>\r\n\r\n<p>Ph&aacute;t biểu của &ocirc;ng Miura kh&ocirc;ng chỉ khiến l&atilde;nh đạo VFF giật m&igrave;nh m&agrave; người h&acirc;m mộ B&oacute;ng đ&aacute; Việt Nam cũng bừng tỉnh khi hiểu r&otilde; hơn về c&aacute;ch điều h&agrave;nh của bộ m&aacute;y VFF.</p>\r\n\r\n<p>Trao đổi với ch&uacute;ng t&ocirc;i cựu danh thủ Thể C&ocirc;ng Vũ Mạnh Hải n&oacute;i thẳng:&nbsp;<em>&ldquo;&Ocirc;ng Miura n&oacute;i rất ch&iacute;nh x&aacute;c. Bởi đơn giản những người điều h&agrave;nh VFF kh&ocirc;ng r&agrave;nh chuy&ecirc;n m&ocirc;n, n&ecirc;n bỏ khuyết một vị tr&iacute; rất quan trọng để gi&uacute;p một nền b&oacute;ng đ&aacute; ph&aacute;t triển bền vững, đ&oacute; l&agrave; gi&aacute;m đốc kỹ thuật&rdquo;</em>.</p>\r\n\r\n<p>&Ocirc;ng Vũ Mạnh Hải c&ograve;n mạnh dạn ph&acirc;n t&iacute;ch về những điểm yếu trong quản l&yacute; điều h&agrave;nh của VFF, đặc biệt l&agrave; việc quản l&yacute; k&eacute;m để h&agrave;ng loạt CLB giải t&aacute;n ngay giữa m&ugrave;a giải, hay kho&aacute;n trắng việc đ&agrave;o tạo trẻ cho c&aacute;c CLB m&agrave; kh&ocirc;ng c&oacute; một sự hỗ trợ n&agrave;o.</p>\r\n\r\n<p><em>&ldquo;L&acirc;u nay c&aacute;ch x&acirc;y dựng đội tuyển của VFF l&aacute; cứ tới đ&acirc;u nhảy tới đ&oacute;, chứ chẳng định hướng g&igrave; cả. Giải n&agrave;o cũng muốn thắng, cũng muốn th&agrave;nh t&iacute;ch m&agrave; kh&ocirc;ng biết rằng mặt bằng của cầu thủ Việt Nam đang ở rất thấp so với c&aacute;c đối thủ. C&aacute;ch quản l&yacute; của VFF khiến ch&uacute;ng ta đang đi chệch hướng, bởi kh&ocirc;ng c&oacute; người giỏi ở thượng tầng n&ecirc;n thiếu những chiến lược ph&aacute;t triển s&acirc;u, bền vững&rdquo;</em>, &ocirc;ng Vũ Mạnh Hải n&oacute;i tiếp.</p>\r\n\r\n<p>HLV Trần B&igrave;nh Sự th&igrave; chỉ ra rằng VFF chỉ l&agrave;m việc theo nhiệm kỳ 5 năm của m&igrave;nh n&ecirc;n kh&ocirc;ng t&iacute;nh đường xa cho B&oacute;ng đ&aacute; Việt Nam m&agrave; chỉ chăm chăm th&agrave;nh t&iacute;ch trước mắt. N&ecirc;n đ&atilde; bao l&acirc;u nay, nền B&oacute;ng đ&aacute; Việt Nam kh&ocirc;ng c&oacute; ch&acirc;n đế v&agrave; rỗng ruột.</p>\r\n\r\n<p>Từ ph&aacute;t biểu của &ocirc;ng Miura, c&aacute;c diễn đ&agrave;n b&oacute;ng đ&aacute; tr&ecirc;n mạng x&atilde; hội s&ocirc;i s&ugrave;ng sục. Rất nhiều &yacute; kiến khen ngợi &ocirc;ng Miura l&agrave; người c&oacute; dũng kh&iacute; bởi d&aacute;m bật &ldquo;&ocirc;ng chủ&rdquo; của m&igrave;nh. Tuy nhi&ecirc;n, vẫn c&oacute; kh&ocirc;ng &iacute;t người lo ngại rằng &ocirc;ng Miura kh&oacute; m&agrave; ở y&ecirc;n mỗi khi đ&atilde; l&agrave;m mất l&ograve;ng một số người ở VFF.</p>\r\n\r\n<p>Thế nhưng, tựu trung lại, người h&acirc;m mộ kỳ vọng ph&aacute;t biểu của &ocirc;ng Miura sẽ g&oacute;p phần gi&uacute;p B&oacute;ng đ&aacute; Việt Nam đổi mới. Bởi c&aacute;c quan chức VFF nếu l&agrave;m việc v&igrave; tương lai của b&oacute;ng đ&aacute; nước nh&agrave; th&igrave; sẽ nh&igrave;n lại v&agrave; thay đổi quyết liệt từ thượng tầng, để B&oacute;ng đ&aacute; Việt Nam thật sự c&oacute; một ch&acirc;n đế vững chắc.</p>\r\n\r\n<p>Về phần m&igrave;nh, mỗi khi đ&atilde; d&aacute;m n&oacute;i thẳng, chứng tỏ &ocirc;ng Miura kh&ocirc;ng sợ mất việc. Khả năng của nh&agrave; cầm qu&acirc;n người Nhật &iacute;t nhiều cũng đ&atilde; được chứng minh, v&igrave; thế, tin rằng &ocirc;ng Miura sẽ tiếp tục ph&aacute;t hiện những yếu k&eacute;m của B&oacute;ng đ&aacute; Việt Nam v&agrave; c&oacute; phương &aacute;n cải thiện một c&aacute;ch tốt nhất để gi&uacute;p đội tuyển Việt Nam ng&agrave;y một mạnh l&ecirc;n.</p>\r\n\r\n<p>Người h&acirc;m mộ tin v&agrave;o &ocirc;ng Miura bởi chỉ 6 th&aacute;ng m&agrave; &ocirc;ng đ&atilde; nh&igrave;n ra rất nhiều vấn đề, chứng tỏ sự nhạy b&eacute;n của chiến lược gia n&agrave;y.</p>\r\n</div>\r\n', '1', '0', '2014-12-23 21:35:04', '2014-12-30 21:54:09', null, null, null, '0');
INSERT INTO `news` VALUES ('13', 'Bầu Đức: ‘Xót xa nhưng phải để cầu thủ U19 đá V-League’', '54997e65484e5.jpg', '20', 'Ông chủ của Hoàng Anh Gia Lai thừa nhận Công Phượng và các đồng đội sẽ đối diện với muôn vàn hiểm nguy khi thi đấu ở V-League, nhưng đó là cách duy nhất để các cầu thủ tích lũy kinh nghiệm.', '<p style=\"text-align:justify\"><span style=\"font-size:14px\"><strong>&Ocirc;ng chủ của Ho&agrave;ng Anh Gia Lai thừa nhận&nbsp;<a href=\"http://beta.bongda.com.vn/tags/nguyen-cong-phuong/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">C&ocirc;ng Phượng</a>&nbsp;v&agrave; c&aacute;c đồng đội sẽ đối diện với mu&ocirc;n v&agrave;n hiểm nguy khi thi đấu ở V-League, nhưng đ&oacute; l&agrave; c&aacute;ch duy nhất để c&aacute;c cầu thủ t&iacute;ch lũy kinh nghiệm.</strong></span></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368130\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 410px;\"><span style=\"font-size:14px\"><a href=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/bauduc17.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\"Bầu Đức chấp nhận &quot;thương đau&quot; để cầu thủ có cơ hội trưởng thành.\" class=\"size-full wp-image-1368130\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/bauduc17.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:400px\" /></a></span>\r\n\r\n<p><span style=\"font-size:14px\">Bầu Đức chấp nhận &ldquo;thương đau&rdquo; để cầu thủ c&oacute; cơ hội trưởng th&agrave;nh.</span></p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">V-League&nbsp;trong t&acirc;m thức của phần đ&ocirc;ng người h&acirc;m mộ được li&ecirc;n hệ đến nhiều kh&iacute;a cạnh ti&ecirc;u cực, m&agrave; nổi cộm l&agrave; t&igrave;nh trạng bạo lực s&acirc;n cỏ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Để &aacute;p chế vấn đề n&agrave;y, từ m&ugrave;a giải trước Li&ecirc;n đo&agrave;n b&oacute;ng đ&aacute; Việt Nam VFF đ&atilde; tỏ ra mạnh tay. Hậu vệ Đ&igrave;nh Đồng (S&ocirc;ng Lam Nghệ An) từng lĩnh &aacute;n cấm thi đấu một năm v&igrave; pha v&agrave;o b&oacute;ng l&agrave;m g&atilde;y ch&acirc;n của Anh H&ugrave;ng (An Giang), tiền vệ Đinh Văn Ta (Ninh B&igrave;nh) bị cấm thi đấu năm trận v&igrave; tung cước v&agrave;o ngực Danny David (Đ&ocirc;ng T&acirc;m Long An), b&ecirc;n cạnh c&aacute;c &aacute;n phạt cho Samson (H&agrave; Nội T&amp;T, ba trận), Văn Nam (Hải Ph&ograve;ng, hết giải), Minh Ch&acirc;u (Hải Ph&ograve;ng, bốn trận), Fagan (Hải Ph&ograve;ng, bốn trận)&hellip;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">&ldquo;<em>Cầu thủ Việt Nam t&iacute;nh chuy&ecirc;n nghiệp chưa cao, nhiều cầu thủ kh&ocirc;ng &yacute; thức được nghề nghiệp của m&igrave;nh v&agrave; kh&ocirc;ng t&ocirc;n trọng đ&ocirc;i ch&acirc;n của đồng nghiệp. V&igrave; thế mới sinh ra bạo lực</em>&ldquo;, ph&oacute; Chủ tịch VFF Đo&agrave;n Nguy&ecirc;n Đức l&yacute; giải. &ldquo;<em>Qua m&ugrave;a giải vừa rồi, t&ocirc;i nghĩ t&igrave;nh trạng bạo lực &iacute;t nhiều thuy&ecirc;n giảm khi c&oacute; những &aacute;n phạt nặng, nghi&ecirc;m khắc v&agrave; kịp thời được đưa ra. C&aacute;c cầu thủ đ&atilde; biết sợ v&agrave; b&oacute;ng đ&aacute; bạo lực sẽ dần mất đi&rdquo;</em>.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Tuy nhi&ecirc;n, người h&acirc;m mộ vẫn kh&ocirc;ng khỏi lo ngại cho&nbsp;<a href=\"http://www.bongda.com.vn/tin-nong/5-cau-thu-dang-xem-nhat-v-league-2015/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">những đ&ocirc;i ch&acirc;n non nớt của Tuấn Anh, Xu&acirc;n Trường, C&ocirc;ng Phượng, Văn To&agrave;n</a>&hellip; khi thi đấu trong đội một của Ho&agrave;ng Anh Gia Lai từ V-League 2015. &ldquo;<em>Khi đưa vấn đề n&agrave;y ra b&agrave;n, c&aacute;c ch&aacute;u tự tin hứa với t&ocirc;i rằng &lsquo;Ch&uacute; cứ cho l&ecirc;n chơi V-League, bọn ch&aacute;u biết phải l&agrave;m g&igrave; để th&iacute;ch nghi v&agrave; n&eacute; tr&aacute;nh&rsquo;. V&igrave; thế, t&ocirc;i cũng thấy an t&acirc;m phần n&agrave;o&rdquo;, bầu Đức chia sẻ. &ldquo;Hơn nữa, d&ugrave; kh&ocirc;ng c&oacute; chủ trương cho cầu thủ của m&igrave;nh ăn thua đủ với đối phương, t&ocirc;i cảm thấy rằng c&aacute;c cầu thủ của m&igrave;nh phải biết chấp nhận v&agrave; đối diện với những hiểm nguy ở V-League. C&oacute; vậy c&aacute;c ch&aacute;u mới trưởng th&agrave;nh được, d&ugrave; đ&uacute;ng l&agrave; rất x&oacute;t xa</em>&ldquo;.</span></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368131\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 500px;\"><span style=\"font-size:14px\"><a href=\"http://a7.img.bongda.com.vn/wp-content/uploads/2014/12/bauduc27.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\"Công Phượng và đồng đội chắc chắn sẽ phải gặp nhiều khó khăn từ lối chơi quyết liệt ở môi trường V-League. \" class=\"size-full wp-image-1368131\" src=\"http://a7.img.bongda.com.vn/wp-content/uploads/2014/12/bauduc27.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:490px\" /></a></span>\r\n\r\n<p><span style=\"font-size:14px\">C&ocirc;ng Phượng v&agrave; đồng đội chắc chắn sẽ phải gặp nhiều kh&oacute; khăn từ lối chơi quyết liệt ở m&ocirc;i trường V-League.</span></p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Trong lứa cầu thủ kh&oacute;a một của học viện HAGL Arsenal JMG, tiền vệ Tuấn Anh từng đứt d&acirc;y chằng, tưởng như phải bỏ nghiệp nhưng hiện tại thi đấu b&igrave;nh thường. Bầu Đức lấy đ&oacute; l&agrave;m v&iacute; dụ để &ldquo;trấn an&rdquo; bản th&acirc;n v&agrave; người h&acirc;m mộ: &ldquo;<em>L&uacute;c thấy Tuấn Anh chấn thương nặng, t&ocirc;i tiếc lắm. Cứ nghĩ rằng vậy l&agrave; c&ocirc;ng toi s&aacute;u năm đ&agrave;o tạo n&ecirc;n một cầu thủ giỏi như thế. Nhưng c&ograve;n nước c&ograve;n t&aacute;t. T&ocirc;i đưa ch&aacute;u sang Ph&aacute;p mổ v&agrave; nối d&acirc;y chằng th&agrave;nh c&ocirc;ng. Giờ th&igrave; Tuấn Anh chạy v&agrave; đ&aacute; ngon l&agrave;nh rồi. T&ocirc;i n&oacute;i vậy để c&aacute;c bạn thấy rằng, lỡ c&oacute; bị b&oacute;ng đ&aacute; bạo lực g&acirc;y ra, cũng phải chấp nhận. V&igrave; đ&oacute; l&agrave; b&oacute;ng đ&aacute; v&agrave; phải c&oacute; những rủi ro. Cầu thủ n&agrave;o lỡ chấn thương th&igrave; t&ocirc;i cho đi điều trị, chăm s&oacute;c tối đa rồi về đ&aacute; tiếp</em>&rdquo;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Chấp nhận &ldquo;thương đau&rdquo; khi đưa những mầm non mới nở l&ecirc;n tham gia đấu trường khốc liệt nhất Việt Nam, nhưng bầu Đức cũng mong c&aacute;c cơ quan chức năng, người h&acirc;m mộ l&ecirc;n &aacute;n những h&agrave;nh vi đ&aacute; xấu trong b&oacute;ng đ&aacute;. &ldquo;<em>C&aacute;c trọng t&agrave;i cần mạnh tay v&agrave; ki&ecirc;n quyết với h&agrave;nh vi đ&aacute; xấu. Truyền th&ocirc;ng v&agrave; người h&acirc;m mộ chắc chắn cũng kh&ocirc;ng đồng t&igrave;nh với b&oacute;ng đ&aacute; bạo lực. T&ocirc;i nghĩ, theo thời gian, c&aacute;c cầu thủ V-League sẽ &yacute; thức được nghề nghiệp của m&igrave;nh. Bởi c&agrave;ng đ&aacute; xấu, đ&aacute; l&aacute;o th&igrave; người h&acirc;m mộ sẽ quay lưng v&agrave; ch&iacute;nh cầu thủ sẽ tự thấy xấu hổ</em>&rdquo;.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:14px\">Để chuẩn bị cho m&ugrave;a b&oacute;ng đầu ti&ecirc;n chinh chiến V-League, thầy tr&ograve; &ocirc;ng Guillaume Graechen đ&atilde; sang Th&aacute;i Lan tập huấn. Họ sẽ về nước một tuần trước ng&agrave;y khai mạc giải.</span></p>\r\n', '1', '0', '2014-12-23 21:38:29', null, null, null, null, '0');
INSERT INTO `news` VALUES ('14', 'Jupp Heynckes khuyên Marco Reus đến Real', '54997ebf57f02.jpg', '7', 'Cựu HLV Bayern Munich, Jupp Heynckes tin rằng Marco Reus nên đến Real Madrid thay vì Bayern Munich.', '<p style=\"text-align:justify\"><strong>Cựu HLV Bayern Munich, Jupp Heynckes tin rằng&nbsp;<a href=\"http://beta.bongda.com.vn/tags/marco-reus\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Marco Reus&nbsp;</a>n&ecirc;n đến<a href=\"http://beta.bongda.com.vn/tags/real-madrid\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">&nbsp;Real Madrid&nbsp;</a>thay v&igrave; Bayern Munich.</strong></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1361230\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 630px;\"><a href=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/reus.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\"Jupp Heynckes khuyên Marco Reus đến Real\" class=\"size-full wp-image-1361230\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/reus.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:620px\" /></a>\r\n\r\n<p>Jupp Heynckes khuy&ecirc;n Marco Reus đến Real</p>\r\n</div>\r\n\r\n<p><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">M</span><span style=\"font-family:tahoma,geneva,sans-serif; font-size:16px\">arco Reus năm nay 25 tuổi, được xem l&agrave; một trong những cầu thủ tấn c&ocirc;ng h&agrave;ng đầu thế giới. Tuyển thủ Đức đang được rất nhiều CLB lớn nhỏ theo đuổi như Bayern, Arsenal, Liverpool, Man United, Barca, Real.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Trong bối cảnh Dortmund thi đấu bết b&aacute;t như hiện tại, đ&atilde; c&oacute; nhiều tin đồn cho rằng Marco Reus sẽ ra đi.</p>\r\n\r\n<p style=\"text-align:justify\"><em>&ldquo;T&ocirc;i sẽ khuy&ecirc;n bạn n&ecirc;n đến Madrid,&rdquo;</em>&nbsp;Heynckes n&oacute;i tr&ecirc;n Sky 90. &ldquo;<em>Bayern đ&atilde; 4 hay 5 cầu thủ đẳng cấp thế giới ở h&agrave;ng c&ocirc;ng.&rdquo;</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&ldquo;Reus sẽ chơi ở đ&acirc;u? Nếu Marco Reus c&oacute; điều khoản ph&aacute; vỡ hợp đồng l&agrave; 25 triệu Euro, tất cả c&aacute;c đội b&oacute;ng lớn của ch&acirc;u &Acirc;u đều n&ecirc;n chi&ecirc;u mộ cậu ấy.&rdquo;</em></p>\r\n\r\n<p style=\"text-align:justify\">Marco Reus gần đ&acirc;y đ&atilde; bị phạt đến 54.000 Euro v&igrave; l&aacute;i xe kh&ocirc;ng giấy ph&eacute;p. Vụ việc n&agrave;y khiến Heynckes tỏ ra rất kh&ocirc;ng h&agrave;i l&ograve;ng.</p>\r\n\r\n<p style=\"text-align:justify\">&Ocirc;ng n&oacute;i:&nbsp;<em>&ldquo;Thật kh&ocirc;ng thể hiểu nổi. Kh&ocirc;ng c&oacute; l&iacute; do n&agrave;o để giải tr&igrave;nh.&rdquo;</em></p>\r\n', '1', '0', '2014-12-23 21:39:59', null, null, null, null, '0');
INSERT INTO `news` VALUES ('15', 'Roque Santa Cruz và 15 năm “quen mặt” tại châu Âu', '54997f20750ab.jpg', '7', 'Với những ai có thâm niên xem bóng đá, Roque Santa Cruz tuy không phải là tiền đạo quá nổi trội, nhưng là một tiền đạo rất dễ “quen mặt thuộc tên”', '<p style=\"text-align:justify\"><strong>Với những ai c&oacute; th&acirc;m ni&ecirc;n xem b&oacute;ng đ&aacute;, Roque Santa Cruz tuy kh&ocirc;ng phải l&agrave; tiền đạo qu&aacute; nổi trội, nhưng l&agrave; một tiền đạo rất dễ &ldquo;quen mặt thuộc t&ecirc;n&rdquo;.</strong></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368469\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 630px;\"><a href=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/SantaAA.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\"Santa Cruz thi đấu thăng hoa nhất khi khoác áo Blackburn\" class=\"size-full wp-image-1368469\" src=\"http://a8.img.bongda.com.vn/wp-content/uploads/2014/12/SantaAA.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:620px\" /></a>\r\n\r\n<p>Santa Cruz thi đấu thăng hoa nhất khi kho&aacute;c &aacute;o Blackburn</p>\r\n</div>\r\n\r\n<p><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">Ở</span><span style=\"font-family:tahoma,geneva,sans-serif; font-size:16px\">tuổi 33,&nbsp;</span><a href=\"http://www.bongda.com.vn/bong-da-tbn/cuu-tien-dao-bayern-munich-man-city-chia-tay-malaga/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; -webkit-transition: all 0.2s ease-in-out; transition: all 0.2s ease-in-out; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;\" title=\"Cựu tiền đạo Bayern Munich, Man City chia tay Malaga\">Roque Santa Cruz đang tr&ecirc;n đường rời CLB Malaga tại La Liga để sang Mexico chơi b&oacute;ng trong những ng&agrave;y cuối c&ugrave;ng của sự nghiệp cầu thủ.</a><span style=\"font-family:tahoma,geneva,sans-serif; font-size:16px\">&nbsp;Ch&acirc;n s&uacute;t người Paraguay sẽ trở th&agrave;nh ng&ocirc;i sao được trả lương cao nhất Mexico l&ecirc;n đến 2 triệu Dollar/ năm (1,6 triệu Euro), cao hơn Ronaldinho 300 ng&agrave;n Euro.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Nhắc đến Roque Santa Cruz, c&aacute;i t&ecirc;n n&agrave;y đ&atilde; xuất hiện từ trước cả năm 2000 bắt đầu. Cụ thể l&agrave; năm 1999, cậu nh&oacute;c Nam Mỹ khi đ&oacute; mới 18 tuổi, thi đấu rất nổi tại CLB qu&ecirc; nh&agrave; Olimpia Asuncion, đ&atilde; được thế lực của b&oacute;ng đ&aacute; ch&acirc;u &Acirc;u thời bấy giờ l&agrave; Bayern Munich chi&ecirc;u mộ.</p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368470\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 560px;\"><a href=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/SantaBB.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\"Santa Cruz có mặt tại Bayern Munich năm mới 18 tuổi\" class=\"size-full wp-image-1368470\" src=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/SantaBB.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:550px\" /></a>\r\n\r\n<p>Santa Cruz c&oacute; mặt tại Bayern Munich năm mới 18 tuổi</p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\">Santa Cruz cũng l&agrave; th&agrave;nh vi&ecirc;n trong đội h&igrave;nh H&ugrave;m x&aacute;m xứ Bavaria đăng quang Champions League năm 2001, được tập luyện v&agrave; thi đấu c&ugrave;ng những Oliver Kahn, Lizarazu, Stefan Effenberg v.v&hellip;</p>\r\n\r\n<p style=\"text-align:justify\">Thế nhưng trong 8 năm gắn b&oacute; đại gia nước Đức (1999-2007), Roque Santa Cruz chỉ sắm vai &ldquo;Si&ecirc;u dự bị&rdquo; với 155 lần ra s&acirc;n, ghi được 31 b&agrave;n, chưa kể m&ugrave;a 2005/06 c&ograve;n phải xuống đ&aacute; cho Bayern II. Lận đận tại Bayern, nhưng ch&acirc;n s&uacute;t người Paraguay cũng tạo cho m&igrave;nh ch&uacute;t tiếng tăm nhất định</p>\r\n\r\n<p style=\"text-align:justify\">Năm 2007, anh khăn g&oacute;i sang Anh kho&aacute;c &aacute;o Blackburn Rovers. Tại CLB mới, được trao cơ hội l&agrave; tiền đạo chủ lực, Santa Cruz c&oacute; những năm th&aacute;ng tươi đẹp với 23 b&agrave;n/ 57 trận ra s&acirc;n trong 2 m&ugrave;a b&oacute;ng, trở th&agrave;nh ch&acirc;n s&uacute;t rất được y&ecirc;u mến.</p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368471\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 560px;\"><a href=\"http://a7.img.bongda.com.vn/wp-content/uploads/2014/12/SantaCC.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\"Cruz là một trong những hợp đồng đầu tiên của Man City sau khi &quot;đổi đời&quot;\" class=\"size-full wp-image-1368471\" src=\"http://a7.img.bongda.com.vn/wp-content/uploads/2014/12/SantaCC.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:550px\" /></a>\r\n\r\n<p>Cruz l&agrave; một trong những hợp đồng đầu ti&ecirc;n của Man City sau khi &ldquo;đổi đời&rdquo;</p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\">Cuối 2008 đầu 2009, Man City được c&aacute;c &ocirc;ng chủ Ả Rập mua lại, tiền đạo cao 1m91 được đưa về s&acirc;n Etihad như &lsquo;vi&ecirc;n gạch&rsquo; đầu ti&ecirc;n x&acirc;y nền m&oacute;ng cho nửa xanh th&agrave;nh Manchester sau n&agrave;y. Nhưng sang Man City l&agrave; bước l&ugrave;i đ&aacute;ng kể trong sự nghiệp của Cruz, anh kh&ocirc;ng thể hiện được nhiều, cộng với việc thay đổi HLV li&ecirc;n tục, nhiều ng&ocirc;i sao kh&aacute;c nhau được đưa về.</p>\r\n\r\n<p style=\"text-align:justify\">Santa Cruz mất dần chỗ đứng v&agrave; sau 2 năm, khi đ&atilde; ở sườn b&ecirc;n kia sự nghiệp anh bị đem cho CLB cũ Blackburn mượn, rồi cho Real Betis mượn. Th&agrave;nh c&ocirc;ng tương đối tại Betis với 7 b&agrave;n/33 trận, Cruz được Malaga mượn m&ugrave;a 2012 v&agrave; mua đứt m&ugrave;a 2013. Tại Malaga, anh cũng c&oacute; đ&oacute;ng g&oacute;p nhất định với 17 b&agrave;n/ 74 trận.</p>\r\n\r\n<p style=\"text-align:justify\">Sau nửa đầu m&ugrave;a b&oacute;ng 2014/15, Cruz c&oacute; được 3 b&agrave;n cho Malaga, đ&aacute;ng ch&uacute; &yacute; l&agrave; 2 b&agrave;n đẳng cấp v&agrave;o lưới Atletico v&agrave; Real Madrid. D&ugrave; vậy, khi nhận thấy kh&ocirc;ng c&ograve;n đ&oacute;ng g&oacute;p được nhiều, cựu tiền đạo trẻ của Bayern Munich quyết định chia tay b&oacute;ng đ&aacute; ch&acirc;u &Acirc;u sau 15 năm lang bạt, kh&eacute;p lại qu&atilde;ng thời gian kh&aacute; d&agrave;i từ Đức sang Anh v&agrave; kết th&uacute;c tại T&acirc;y Ban Nha.</p>\r\n', '1', '0', '2014-12-23 21:41:36', null, '1', '0', '0', '0');
INSERT INTO `news` VALUES ('16', 'Ronaldo và Messi: Cuộc chiến giữa 2 thương hiệu triệu đô', '54997f7f9fb06.jpg', '9', 'Hãy cùng xem thử Ronaldo và Messi vào thời điểm hiện tại “so găng” thế nào bên ngoài sân cỏ.', '<p style=\"text-align:justify\"><strong>H&atilde;y c&ugrave;ng xem thử&nbsp;<a href=\"http://beta.bongda.com.vn/tags/Cristiano-Ronaldo\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Ronaldo</a>&nbsp;v&agrave;&nbsp;<a href=\"http://beta.bongda.com.vn/tags/Lionel-Messi\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Messi</a>&nbsp;v&agrave;o thời điểm hiện tại &ldquo;so găng&rdquo; thế n&agrave;o b&ecirc;n ngo&agrave;i s&acirc;n cỏ.</strong></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368363\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 610px;\"><a href=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien1.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\" Ronaldo và Messi: Cuộc chiến giữa 2 thương hiệu triệu đô \" class=\"size-full wp-image-1368363\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien1.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:600px\" /></a>\r\n\r\n<p>Messi &ndash; Ronaldo, cuộc chiến kh&ocirc;ng hồi kết.</p>\r\n</div>\r\n\r\n<p><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">M</span><span style=\"font-family:tahoma,geneva,sans-serif; font-size:16px\">ột lần nữa,&nbsp;</span><a href=\"http://www.bongda.com.vn/bong-da-tbn/qua-bong-vang-2014-messi-van-la-moi-de-doa-lon-nhat-cua-ronaldo/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; -webkit-transition: all 0.2s ease-in-out; transition: all 0.2s ease-in-out; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; line-height: 24px; text-align: justify;\" target=\"_blank\" title=\"Quả bóng vàng 2014 – Messi vẫn là mối đe dọa lớn nhất của Ronaldo\">Ronaldo v&agrave; Messi lại tiếp tục đối đầu nhau trong cuộc đua đến danh hiệu QBV FIFA.&nbsp;</a><span style=\"font-family:tahoma,geneva,sans-serif; font-size:16px\">Mặc d&ugrave; thủ m&ocirc;n Bayern Munich l&agrave; Manuel Neuer cũng được đề cử cho giải thưởng n&agrave;y, nhưng so với El Pulga v&agrave; CR7 th&igrave; r&otilde; r&agrave;ng l&agrave; Neuer l&eacute;p vế hơn hẳn, kể cả khi tuyển thủ người Đức đ&atilde; n&acirc;ng chiếc c&uacute;p v&agrave;ng World Cup tại Brazil m&ugrave;a h&egrave; vừa qua. Tuy nhi&ecirc;n, sự ganh đua giữa 2 ch&acirc;n s&uacute;t xuất sắc nhất thế giới đương đại kh&ocirc;ng chỉ giới hạn tr&ecirc;n s&acirc;n cỏ.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i s&acirc;n cỏ, Messi v&agrave; Ronaldo c&oacute; tầm ảnh hưởng trải rộng khắp to&agrave;n cầu. Mỗi &ldquo;status&rdquo; quảng c&aacute;o tr&ecirc;n Facebook của họ trị gi&aacute; h&agrave;ng chục ngh&igrave;n USD, sức h&uacute;t từ t&ecirc;n tuổi của cặp đ&ocirc;i n&agrave;y cũng gi&uacute;p c&aacute;c thương hiệu lớn nhỏ đẩy mạnh lượng sản phẩm ti&ecirc;u thụ. Như vậy th&igrave;, ai trong số 2 si&ecirc;u sao n&agrave;y mới l&agrave; người &ldquo;c&oacute; gi&aacute;&rdquo; tr&ecirc;n thị trường nhất? L&agrave; Messi, cầu thủ vừa ph&aacute; kỷ lục ghi b&agrave;n tại La Liga cũng như Champions League, hay l&agrave; Ronaldo, đương kim chủ nh&acirc;n của QBV?</p>\r\n\r\n<p style=\"text-align:justify\">Mới đ&acirc;y, c&ocirc;ng ty chuy&ecirc;n nghi&ecirc;n cứu thị trường thể thao Repucom đ&atilde; tiến h&agrave;nh điều tra 15 thị trường kh&aacute;c nhau tr&ecirc;n thế giới, trải rộng từ T&acirc;y Ban Nha đến Mỹ, để rồi ph&aacute;t hiện rằng si&ecirc;u sao của Real Madrid, Cristiano Ronaldo, mới l&agrave; người được biết đến nhiều nhất. Theo Repucom, 92% người được hỏi đều biết đến CR7, c&ograve;n với Messi th&igrave; l&agrave; 87%. Một điều th&uacute; vị nữa l&agrave;, tỷ lệ nam giới biết về Messi cũng bằng với tỷ lệ nữ giới biết về Ronaldo &ndash; 91%.</p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368364\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 610px;\"><a href=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien2.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\" Ronaldo và Messi: Cuộc chiến giữa 2 thương hiệu triệu đô \" class=\"size-full wp-image-1368364\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien2.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:600px\" /></a>\r\n\r\n<p>Ronaldo được biết đến nhiều hơn Messi.</p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\">Cụ thể hơn nữa, với giới trẻ ở độ tuổi 18-24, c&oacute; 94% biết đến Ronaldo c&ograve;n 93% biết đến Messi. Tuy nhi&ecirc;n, được biết đến kh&ocirc;ng đồng nghĩa với việc được y&ecirc;u th&iacute;ch. Trong nh&oacute;m độ tuổi n&agrave;y, 72% cho biết m&igrave;nh l&agrave; người h&acirc;m mộ của Messi c&ograve;n với Ronaldo th&igrave; l&agrave; 70%. Mặt kh&aacute;c, Messi được nh&oacute;m tuổi tr&ecirc;n 55 y&ecirc;u th&iacute;ch nhất, với 80% trong số 81% biết đến anh n&oacute;i rằng họ th&iacute;ch anh. Trong khi đ&oacute;, c&oacute; tận 90% biết đến Ronaldo nhưng chỉ c&oacute; 67% th&iacute;ch anh m&agrave; th&ocirc;i. Ngo&agrave;i ra, nơi &iacute;t biết đến Messi v&agrave; Ronaldo nhất ch&iacute;nh l&agrave; nước Mỹ (chỉ c&oacute; 30% biết El Pulga v&agrave; 40% biết CR7).</p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368365\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 610px;\"><a href=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien3.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\" Ronaldo và Messi: Cuộc chiến giữa 2 thương hiệu triệu đô \" class=\"size-full wp-image-1368365\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien3.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:600px\" /></a>\r\n\r\n<p>Messi được y&ecirc;u th&iacute;ch nhiều hơn Ronaldo.</p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh đ&oacute;, Repucom c&ograve;n cho biết thương hiệu của Ronaldo c&oacute; vẻ b&aacute;n chạy hơn hẳn, với 86% người được hỏi n&oacute;i rằng đội trưởng của tuyển Bồ Đ&agrave;o Nha l&agrave;<em>&nbsp;&ldquo;một người ph&aacute;t ng&ocirc;n c&oacute; sức ảnh hưởng lớn&rdquo;</em>. Theo lời Mike Wragg, trưởng nh&oacute;m nghi&ecirc;n cứu to&agrave;n cầu của Repucom,<em>&nbsp;&ldquo;Cristiano Ronaldo vượt trội về &lsquo;t&iacute;nh ti&ecirc;u thụ&rsquo; to&agrave;n diện bởi v&igrave; ng&agrave;y c&agrave;ng c&oacute; nhiều người biết đến anh ấy tr&ecirc;n khắp thế giới. Trong khi đ&oacute;, lượng người biết đến Messi v&igrave; họ th&iacute;ch anh ấy lại nhiều hơn&rdquo;.</em>&nbsp;Chốt lại, Repucom khẳng định Ronaldo l&agrave; cầu thủ c&oacute; t&iacute;nh ti&ecirc;u thụ cao nhất tr&ecirc;n thị trường.</p>\r\n\r\n<p style=\"text-align:justify\">Để giải th&iacute;ch cho việc n&agrave;y, Simon Chadwick, gi&aacute;o sư của Chiến lược Kinh doanh Thể thao v&agrave; Tiếp thị ở Đại học Coventry n&oacute;i với tờ CNN:&nbsp;<em>&ldquo;Từ một người từng được biết đến với th&oacute;i &iacute;ch kỷ v&agrave; ngạo mạn, gần đ&acirc;y h&igrave;nh ảnh của Ronaldo đ&atilde; mềm mại hơn trong mắt c&ocirc;ng ch&uacute;ng nhờ h&agrave;nh động trả tiền trị bệnh cho một fan nh&iacute;, chưa kể việc Ronaldo c&ograve;n cắt t&oacute;c để đ&aacute;nh dấu mối quan hệ với cậu b&eacute; đ&oacute; (kiểu t&oacute;c tr&ugrave;ng với vết sẹo phẫu thuật của cậu b&eacute;). Ch&iacute;nh những h&agrave;nh động tr&ecirc;n đ&atilde; cấu th&agrave;nh v&agrave; định hướng cho thương hiệu Ronaldo&rdquo;.</em></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368366\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 610px;\"><a href=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien4.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\" Ronaldo và Messi: Cuộc chiến giữa 2 thương hiệu triệu đô \" class=\"size-full wp-image-1368366\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien4.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:600px\" /></a>\r\n\r\n<p>Kiểu t&oacute;c đặc biệt của Ronaldo được cho l&agrave; để ủng hộ fan nh&iacute; bị bệnh u n&atilde;o, tuy nhi&ecirc;n nhiều tờ b&aacute;o cho biết &yacute; tưởng của kiểu t&oacute;c n&agrave;y kh&ocirc;ng phải l&agrave; vậy.</p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\">Kh&ocirc;ng chỉ vậy, Ronaldo cũng &ldquo;c&oacute; gi&aacute;&rdquo; hơn Messi tr&ecirc;n mạng x&atilde; hội. Hiện tại, CR7 đang c&oacute; hơn 103 triệu lượt &ldquo;like&rdquo; tr&ecirc;n Facebook với mức gi&aacute; trung b&igrave;nh cho mỗi một b&agrave;i quảng c&aacute;o v&agrave;o khoảng 143.750 USD (~ 3 tỷ đồng). Trong khi đ&oacute;, Messi chỉ c&oacute; 76 triệu lượt &ldquo;like&rdquo; v&agrave; mức gi&aacute; quảng c&aacute;o &iacute;t hơn ph&acirc;n nửa so với đại k&igrave;nh địch, 62.500 USD (~ 1,34 tỷ đồng).</p>\r\n\r\n<p style=\"text-align:justify\">Khi được hỏi liệu Messi c&oacute; được quảng b&aacute; theo c&aacute;ch kh&aacute;c để bắt kịp Ronaldo trong tương lai hay kh&ocirc;ng, Chadwick cho biết:&nbsp;<em>&ldquo;T&ocirc;i kh&ocirc;ng cho l&agrave; bản th&acirc;n Messi hay c&aacute;c cố vấn của cậu ấy muốn việc n&agrave;y. T&ocirc;i nghĩ rằng cậu ấy hạnh ph&uacute;c với việc được chơi b&oacute;ng v&agrave; chỉ nhặt một &iacute;t tiền quảng c&aacute;o vậy th&ocirc;i&rdquo;</em>. Ngo&agrave;i ra, Chadwick c&ograve;n chia sẻ th&ecirc;m<em>: &ldquo;5 năm trước, thương hiệu của Messi thuộc dạng ng&acirc;y thơ trẻ con, cậu ấy l&agrave; h&igrave;nh tượng của một cậu b&eacute; chơi b&oacute;ng v&igrave; niềm y&ecirc;u th&iacute;ch. Nhưng b&acirc;y giờ, cậu ấy đ&atilde; c&oacute; đứa con của ri&ecirc;ng m&igrave;nh, thế n&ecirc;n cậu ấy kh&ocirc;ng c&ograve;n l&agrave; &lsquo;cậu b&eacute;&rsquo; nữa. Th&ecirc;m cả rắc rối xoay quanh việc trốn thuế cũng khiến h&igrave;nh ảnh của cậu ấy kh&ocirc;ng c&ograve;n ng&acirc;y thơ như ng&agrave;y xưa&rdquo;.</em></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368367\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 610px;\"><a href=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien5.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\" Ronaldo và Messi: Cuộc chiến giữa 2 thương hiệu triệu đô \" class=\"size-full wp-image-1368367\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien5.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:600px\" /></a>\r\n\r\n<p>H&igrave;nh ảnh của Messi bị xấu đi v&igrave; vụ trốn thuế.</p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\">Mặt kh&aacute;c, nếu quay về lĩnh vực s&acirc;n cỏ, Messi c&oacute; những số liệu vượt trội hơn hẳn, nhất l&agrave; khi El Pulga trẻ hơn CR7 đến 2 tuổi. Hiện tại, ch&acirc;n s&uacute;t Barcelona đang l&agrave; cầu thủ ghi nhiều b&agrave;n nhất trong lịch sử La Liga với 258 b&agrave;n thắng (con số n&agrave;y sẽ c&ograve;n tăng nữa), trong khi Ronaldo chỉ c&oacute; 203 b&agrave;n. Kh&ocirc;ng chỉ vậy, Messi c&ograve;n l&agrave; cầu thủ ghi nhiều b&agrave;n nhất lịch sử Champions League với 75 b&agrave;n cả thảy, c&ograve;n Ronaldo b&aacute;m s&aacute;t ph&iacute;a sau với 71 b&agrave;n. Ngo&agrave;i ra, si&ecirc;u sao người Argentina cũng giữ kỷ lục về số lượng QBV với 4 lần đăng quang c&ograve;n Ronaldo chỉ mới c&oacute; 2, nhưng theo nhiều chuy&ecirc;n gia dự đo&aacute;n th&igrave; c&oacute; lẽ CR7 sẽ kịp r&uacute;t ngắn khoảng c&aacute;ch n&agrave;y tại Zurich v&agrave;o th&aacute;ng 1 tới.</p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368368\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 610px;\"><a href=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien6.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\" Ronaldo và Messi: Cuộc chiến giữa 2 thương hiệu triệu đô \" class=\"size-full wp-image-1368368\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien6.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:600px\" /></a>\r\n\r\n<p>Những số liệu trong sự nghiệp của 2 si&ecirc;u sao.</p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\">Về phần th&agrave;nh t&iacute;ch ở đội tuyển, cả hai đều c&oacute; điểm chung l&agrave; kh&ocirc;ng mấy th&agrave;nh c&ocirc;ng khi kho&aacute;c l&ecirc;n m&igrave;nh m&agrave;u &aacute;o của nước nh&agrave;. D&ugrave; vậy, Ronaldo vẫn l&agrave; ch&acirc;n s&uacute;t ghi nhiều b&agrave;n nhất cho đội tuyển Bồ Đ&agrave;o Nha với 52 b&agrave;n, trong khi Messi đồng giữ kỷ lục về số b&agrave;n thắng trong 1 năm cho Argentina c&ugrave;ng cựu danh thủ Gabriel Batistuta. Mặt kh&aacute;c, El Pulga hiện đang xếp thứ 2 trong danh s&aacute;ch những ch&acirc;n s&uacute;t ghi nhiều b&agrave;n nhất cho La Albiceleste với 45 b&agrave;n, chỉ thua mỗi Batistuta (56 b&agrave;n). Ở độ tuổi 27, Messi vẫn c&ograve;n rất nhiều thời gian để san bằng kỷ lục của tiền bối.</p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368362\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 610px;\"><a href=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien7.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\" Ronaldo và Messi: Cuộc chiến giữa 2 thương hiệu triệu đô \" class=\"size-full wp-image-1368362\" src=\"http://a9.img.bongda.com.vn/wp-content/uploads/2014/12/cuocchien7.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:600px\" /></a>\r\n\r\n<p>D&ugrave; chưa gi&agrave;nh được danh hiệu to lớn g&igrave; c&ugrave;ng đội tuyển, nhưng cả hai đều ghi rất nhiều b&agrave;n thắng trong m&agrave;u &aacute;o nước nh&agrave;</p>\r\n</div>\r\n', '1', '1', '2014-12-23 21:43:11', null, '3', '2', '0', '0');
INSERT INTO `news` VALUES ('17', 'Kovacic – Trái tim và hơi thở của Inter Milan', '54997fe942c6a.jpg', '8', ' Những ngày cuối cùng của kỳ chuyển nhượng mùa Đông năm 2013, ban lãnh đạo của Inter đã chi ra hơn 15 triệu Euro để mang về một cậu nhóc 18 tuổi người Croatia từ Dinamo Zagreb với hi vọng cậu bé ...', '<p style=\"text-align:justify\"><strong>Những ng&agrave;y cuối c&ugrave;ng của kỳ chuyển nhượng m&ugrave;a Đ&ocirc;ng năm 2013, ban l&atilde;nh đạo của&nbsp;<a href=\"http://bongda.com.vn/tags/Inter-Milan/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Inter</a>&nbsp;đ&atilde; chi ra hơn 15 triệu Euro để mang về một cậu nh&oacute;c 18 tuổi người Croatia từ Dinamo Zagreb với hi vọng cậu b&eacute; sẽ l&agrave; tương lai của Nerazzurri sau thời kỳ suy tho&aacute;i của đế chế La Grande Inter II. Giờ đ&acirc;y cậu b&eacute; đ&oacute; đ&atilde; trưởng th&agrave;nh thật sự v&agrave; đ&atilde; trở th&agrave;nh tr&aacute;i tim của Inter dưới thời Roberto Mancini khi được thi đấu với đ&uacute;ng vị tr&iacute; sở trường v&agrave; ph&aacute;t huy hết những phẩm chất thi&ecirc;n t&agrave;i của m&igrave;nh &ndash; Ch&agrave;ng trai 20 tuổi đến từ v&ugrave;ng Balkan:&nbsp;<a href=\"http://bongda.com.vn/tags/Mateo-Kovacic/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Mateo Kovacic</a>.</strong></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368458\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 660px;\"><img alt=\"Mateo Kovacic đang là cầu thủ quan trọng của Inter Milan. Ảnh internet\" class=\"size-full wp-image-1368458\" src=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/MateoKovacic.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:650px\" />\r\n<p>Mateo Kovacic đang l&agrave; cầu thủ quan trọng của Inter Milan. Ảnh internet</p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">I</span>nter vĩ đại của năm 2010 đ&atilde; chết dần chết m&ograve;n theo năm th&aacute;ng v&agrave; cho đến năm 2013 th&igrave; kh&ocirc;ng c&ograve;n một ch&uacute;t g&igrave; c&oacute; thể xem l&agrave; &ldquo;bản sắc&rdquo; của Inter dưới thời Mourinho. Đội b&oacute;ng chủ s&acirc;n Meazza l&agrave; một mớ hỗn độn với một huấn luyện vi&ecirc;n c&ograve;n non trẻ như Stramaccioni v&agrave; kh&ocirc;ng định hướng trong ch&iacute;nh s&aacute;ch chuyển nhượng cũng như ph&aacute;t triển về kinh tế.</p>\r\n\r\n<p style=\"text-align:justify\">Inter Milan l&acirc;m v&agrave;o khủng hoảng v&agrave; mua b&aacute;n một c&aacute;ch tr&agrave;n lan. Họ b&aacute;n Coutinho cho Liverpool để rồi tiền vệ người Brazil tỏa s&aacute;ng trong m&agrave;u &aacute;o Đỏ của đội b&oacute;ng th&agrave;nh phố cảng xứ sương m&ugrave;. G&atilde; khổng lồ Barcelona c&ugrave;ng Liverpool tiếp tục dạm hỏi Kovacic trong kỳ chuyển nhượng m&ugrave;a h&egrave; vừa qua với Kovacic nhưng thật may mắn, Thohir c&ugrave;ng bộ sậu đ&atilde; kh&ocirc;ng dại dột đem cho đi một vi&ecirc;n ngọc qu&yacute;.</p>\r\n\r\n<p style=\"text-align:justify\">Kovacic dưới thời Mazzarri lu&ocirc;n phải thi đấu thấp v&agrave; lệch về b&ecirc;n tr&aacute;i của h&agrave;ng tiền vệ đ&ocirc;ng người. D&ugrave; vẫn lu&ocirc;n đi b&oacute;ng dễ d&agrave;ng qua 2, thậm ch&iacute; 3 cầu thủ đối phương nhưng sơ đồ chiến thuật của Mazzarri kh&ocirc;ng cho ph&eacute;p Kovacic ph&aacute;t huy được hết những g&igrave; m&igrave;nh c&oacute;. Cho đến khi Mancio được đưa về thay thế cho &ocirc;ng thầy bảo thủ lu&ocirc;n ra s&acirc;n với sơ đồ 3&ndash;5&ndash;2 th&igrave; Kovacic mới thật sự l&agrave; ch&iacute;nh m&igrave;nh. Tiền vệ người Croatia như được giải tho&aacute;t, anh được đẩy l&ecirc;n đ&aacute; ngay sau lưng 2 tiền đạo trong sơ đồ 4&ndash;3&ndash;1&ndash;2 của Mancini.</p>\r\n\r\n<p style=\"text-align:justify\">Ở vị tr&iacute; n&agrave;y, Kovacic sử dụng tốc độ v&agrave; kỹ thuật c&aacute; nh&acirc;n kh&eacute;o l&eacute;o dễ d&agrave;ng vượt qua tiền vệ ph&ograve;ng ngự của đối phương rồi tung những đường chuyền sắc lẹm cho Icardi v&agrave; Palacio ở ph&iacute;a tr&ecirc;n hay đột ph&aacute; thẳng v&agrave;o trung lộ rồi tung những c&uacute; dứt điểm mạnh như b&uacute;a bổ. Quan trọng hơn cả l&agrave; khả năng x&acirc;m nhập v&ograve;ng cấm v&agrave; ghi b&agrave;n. Đối với một Trequartista th&igrave; ghi b&agrave;n l&agrave; một việc kh&ocirc;ng thể thiếu để đ&oacute;ng g&oacute;p v&agrave;o lối chơi cũng như th&agrave;nh t&iacute;ch của đội nh&agrave;.</p>\r\n\r\n<p style=\"text-align:justify\">So s&aacute;nh một ch&uacute;t để hiểu được việc Mancini v&agrave; Mazzarri đ&atilde; sử dụng Kovacic như thế n&agrave;o. Dưới thời Mazzarri m&ugrave;a giải 2013/2014, Kovacic ra s&acirc;n 32 trận nhưng c&oacute; đến 18 trận phải v&agrave;o s&acirc;n từ băng ghế dự bị, thi đấu tổng cộng 1605 ph&uacute;t v&agrave; kh&ocirc;ng ghi nổi một b&agrave;n thắng c&ograve;n Kovacic dưới thời Mancini thi đấu 5 trận ở Serie A với 374 ph&uacute;t c&oacute; mặt tr&ecirc;n s&acirc;n nhưng đ&atilde; ghi được 2 b&agrave;n thắng.</p>\r\n\r\n<p style=\"text-align:justify\">Với Kovacic thi đấu nh&ocirc; cao, được bao bọc b&ecirc;n dưới bởi 3 tiền vệ c&oacute; xu hướng ph&ograve;ng ngự nhiều hơn th&igrave; Inter lu&ocirc;n kiểm so&aacute;t rất tốt khu trung tuyến. Trong trận đấu với Lazio, d&ugrave; đ&atilde; thủng lưới đến 2 b&agrave;n nhưng phần nhiều trong 2 b&agrave;n thua l&agrave; do lỗi của c&aacute;c trung vệ v&agrave; hậu vệ c&aacute;nh phải Danilo D&rsquo;Ambrossio c&ugrave;ng sự xuất sắc đến kinh ngạc của tiền vệ Felipe Anderson b&ecirc;n ph&iacute;a đội kh&aacute;ch.</p>\r\n\r\n<p style=\"text-align:justify\">Kovacic thi đấu ở vị tr&iacute; Trequartista đ&atilde; c&oacute; nhiều khoảng trống hơn trước khung th&agrave;nh cũng như c&oacute; nhiều cơ hội dứt điểm hơn trước khu vực 16m50 v&agrave; c&uacute; Volley ch&aacute;y lưới Marchetti trong trận đấu với Lazio ở v&ograve;ng đấu 16 ch&iacute;nh l&agrave; minh chứng cho sự đ&uacute;ng đắn của Mancini. Ở tuổi 20, ch&agrave;ng trai sinh ra tại Linz (&Aacute;o) n&agrave;y đ&atilde; to lớn hơn đ&aacute;ng kể so với 2 năm trước khi mới chập chững đặt ch&acirc;n l&ecirc;n thảm cỏ Giuseppe Meazza. Mateo Kovacic đ&atilde; nặng 80 kg v&agrave; c&oacute; một cơ thể săn chắc hơn rất nhiều.</p>\r\n\r\n<p style=\"text-align:justify\">Giờ đ&acirc;y, từ một cậu nh&oacute;c mặt c&ograve;n b&uacute;ng ra sữa ở tuổi 18, Kovacic đ&atilde; v&agrave; đang dần trưởng th&agrave;nh ở tuổi 20. Khu&ocirc;n mặt vẫn l&agrave; n&eacute;t hiền h&ograve;a dễ mến ấy nhưng đ&ocirc;i ch&acirc;n v&agrave; bản lĩnh th&igrave; đ&atilde; vững chắc th&ecirc;m nhiều phần. C&aacute;c hậu vệ của đối phương vẫn li&ecirc;n tục c&oacute; những pha phạm lỗi nguy hiểm trước những pha đi b&oacute;ng như x&eacute; gi&oacute; của Kovacic v&igrave; chỉ c&oacute; như vậy mới ngăn cản được anh.</p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c chuy&ecirc;n gia đ&atilde; gọi anh l&agrave; Messi của Đ&ocirc;ng &Acirc;u, huyền thoại Boban đ&atilde; ca ngợi Kovacic như l&agrave; truyền nh&acirc;n của m&igrave;nh ở đội tuyển Croatia c&ograve;n vị huấn luyện vi&ecirc;n l&atilde;o l&agrave;ng Giovanni Trapattoni th&igrave; hết lời khen ngợi số 10 của Inter ch&iacute;nh l&agrave; sự kết hợp ho&agrave;n hảo của Kaka v&agrave; Seedorf. Vẫn biết l&agrave; những lời khen của những tiền bối lu&ocirc;n l&agrave; sự kh&iacute;ch lệ cũng như thừa nhận t&agrave;i năng của tiền vệ người Croatia nhưng đ&oacute; cũng ch&iacute;nh l&agrave; sức &eacute;p m&agrave; Kovacic phải vượt qua để tiến l&ecirc;n một nấc thang cao hơn nữa v&agrave; c&ugrave;ng Nerazzurri vượt qua giai đoạn kh&oacute; khăn n&agrave;y.</p>\r\n\r\n<p style=\"text-align:justify\">Inter của tương lai tr&ocirc;ng đợi cả v&agrave;o Kovacic v&agrave; Kovacic chứ kh&ocirc;ng phải ai kh&aacute;c &ndash; ch&iacute;nh l&agrave; tr&aacute;i tim, khối &oacute;c v&agrave; hơi thở của Nerazzurri của hiện tại v&agrave; cả ng&agrave;y mai!</p>\r\n', '1', '1', '2014-12-23 21:44:57', null, '1', '0', '0', '0');
INSERT INTO `news` VALUES ('18', 'Đội hình tiêu biểu Serie A vòng 16: “Vua sư tử” trở lại', '54998030a9291.jpg', '8', 'Với màn trình diễn ấn tượng trong trận đấu với Cagliari, “Vua sư tử” Llorenteđã trở lại Đội hình tiêu biểu Serie A vòng 16.', '<p style=\"text-align:justify\"><strong>Với m&agrave;n tr&igrave;nh diễn ấn tượng trong trận đấu với Cagliari, &ldquo;Vua sư tử&rdquo;&nbsp;<a href=\"http://bongda.com.vn/tags/Fernando-Llorente/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Llorente</a>đ&atilde; trở lại Đội h&igrave;nh ti&ecirc;u biểu&nbsp;<a href=\"http://www.bongda.com.vn/cat/bong-da-y/serie-a/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Serie A</a>&nbsp;v&ograve;ng 16.</strong></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368441\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 696px;\"><img alt=\"Đội hình tiêu biểu Serie A vòng 16: “Vua sư tử” trở lại\" class=\"size-full wp-image-1368441\" src=\"http://a7.img.bongda.com.vn/wp-content/uploads/2014/12/PhilippeMexes.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:686px\" />\r\n<p>Philippe Mexes . Ảnh internet</p>\r\n</div>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">T</span>hủ m&ocirc;n</strong></p>\r\n\r\n<p style=\"text-align:justify\">Nicola Leali (Cesena): Trong ng&agrave;y thi đấu xuất thần, thủ th&agrave;nh Leali đ&atilde; c&oacute; 8 pha cứu thua cho Cesena, gi&uacute;p CLB gi&agrave;nh được 1 điểm qu&yacute; gi&aacute; tr&ecirc;n s&acirc;n của Sassuolo.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Hậu vệ</strong></p>\r\n\r\n<p style=\"text-align:justify\">Maggio (Napoli): C&aacute;nh phải của Napoli đ&atilde; được bảo vệ an to&agrave;n bởi Maggio. Hậu vệ n&agrave;y đ&atilde; c&oacute; 2 c&uacute; tắc b&oacute;ng, 4 lần thu hồi b&oacute;ng th&agrave;nh c&ocirc;ng. Ngo&agrave;i ra, anh cũng đ&oacute;ng g&oacute;p 1 đường chuyền th&agrave;nh b&agrave;n cho Napoli.</p>\r\n\r\n<p style=\"text-align:justify\">Kamil Glik (Torino): 3 c&uacute; tắc b&oacute;ng, 6 lần giải nguy, Glik tựa như bức tường th&eacute;p trước khung th&agrave;nh của Torino, gi&uacute;p đội nh&agrave; giữ sạch lưới trước Genoa. Quan trọng hơn, trung vệ n&agrave;y c&ograve;n đ&oacute;ng g&oacute;p c&uacute; đ&uacute;p, gi&uacute;p Torino gi&agrave;nh chiến thắng 2-1.</p>\r\n\r\n<p style=\"text-align:justify\">Philippe Mexes (AC Milan): Mexes đ&atilde; chỉ huy rất tốt h&agrave;ng thủ của AC Milan đứng vững trước AS Roma, đặc biệt l&agrave; khi Rossoneri l&acirc;m v&agrave;o thế thiếu người trong 20 ph&uacute;t cuối.</p>\r\n\r\n<p style=\"text-align:justify\">Faouzi Ghoulam (Napoli): Ghoulam đ&atilde; cho thấy khả năng ph&ograve;ng ngự tuyệt vời. Trong trận đấu với Parma, cầu thủ n&agrave;y đ&atilde; c&oacute; 5 c&uacute; tắc b&oacute;ng, 5 lần đ&aacute;nh chặn th&agrave;nh c&ocirc;ng, g&oacute;p c&ocirc;ng lớn v&agrave;o chiến thắng của Empoli.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Tiền vệ</strong></p>\r\n\r\n<p style=\"text-align:justify\">Artuto Vidal (Juventus): Vidal gần như &ldquo;chấp hết&rdquo; h&agrave;ng tiền vệ của Cagliari. Anh c&oacute; 5 c&uacute; tắc b&oacute;ng, 5 lần giải nguy v&agrave; đ&oacute;ng g&oacute;p 1 b&agrave;n thắng tuyệt đẹp, gi&uacute;p Juventus chiến thắng v&agrave; l&ecirc;n ng&ocirc;i v&ocirc; địch m&ugrave;a Đ&ocirc;ng.</p>\r\n\r\n<p style=\"text-align:justify\">Franco V&aacute;zquez (Palermo): V&aacute;zquez đ&atilde; đặt dấu gi&agrave;y v&agrave;o cả 3 b&agrave;n thắng của Palermo trong trận h&ograve;a 3-3 với Atalanta.</p>\r\n\r\n<p style=\"text-align:justify\">Kovacic (Inter): Kovacic đang ng&agrave;y một thăng hoa trong vai tr&ograve; mới (hộ c&ocirc;ng) dưới triều đại Mancini. Ở v&ograve;ng n&agrave;y, tiền vệ người Croatia đ&atilde; đ&oacute;ng g&oacute;p một si&ecirc;u phẩm trong chiến t&iacute;ch lội ngược d&ograve;ng của Inter trước Lazio.</p>\r\n\r\n<p style=\"text-align:justify\">Juan Vargas (Fiorentina): Tiền vệ người Peru l&agrave; &ldquo;ng&ograve;i nổ&rdquo; nguy hiểm b&ecirc;n c&aacute;nh tr&aacute;i của Fiorentina. Ở trận đấu với Empoli, cầu thủ n&agrave;y đ&atilde; ghi 1 b&agrave;n thắng v&agrave; tung ra 5 đường kiến tạo cơ hội (nhưng kh&ocirc;ng được đồng đội chuyển h&oacute;a th&agrave;nh b&agrave;n thắng).</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Tiền đạo</strong></p>\r\n\r\n<p style=\"text-align:justify\">Denis (Atalanta): Nếu như Palermo c&oacute; Varquez xuất sắc th&igrave; Atalanta cũng c&oacute; Denis rực s&aacute;ng. Tiền đạo người Argentina đ&atilde; đ&oacute;ng g&oacute;p 2 b&agrave;n thắng trong trận h&ograve;a nghẹt thở với tỷ số 3-3 của hai đội.</p>\r\n\r\n<p style=\"text-align:justify\">Llorente (Juventus): Tiền đạo người T&acirc;y Ban Nha đ&atilde; c&oacute; rất nhiều lần uy hiếp khung th&agrave;nh của Cagliari v&agrave; đ&oacute;ng g&oacute;p b&agrave;n thắng đầu ti&ecirc;n sau 4 trận li&ecirc;n tiếp. Với đ&oacute;ng g&oacute;p của m&igrave;nh, &ldquo;Vua sư tử&rdquo; Llorente xứng đ&aacute;ng được t&ocirc;n vinh.</p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1368442\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 660px;\"><img alt=\"Đội hình tiêu biểu Serie A vòng 16: “Vua sư tử” trở lại\" class=\"size-full wp-image-1368442\" src=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/PhilippeMexes1.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:650px\" />\r\n<p>Fernando Llorente. Ảnh internet</p>\r\n</div>\r\n', '1', '0', '2014-12-23 21:46:08', null, '1', '0', '0', '0');
INSERT INTO `news` VALUES ('19', 'Man City và PSG: Phục hận được không, hai gã nhà giàu?', '54998118e8c56.jpg', '9', ' Manchester City chạm trán Barcelona, PSG lại được dịp đối đầu với Chelsea. Cả hai gã nhà giàu Man City và PSG mùa giải năm ngoái đều lần lượt ngã ngựa trước Barca và Chelsea. Cơ hội rửa hận đang đến gần hơn bao giờ hết, vấn đề là họ có thể tận dụng được hay không mà thôi.', '<p style=\"text-align:justify\"><strong>&nbsp;<a href=\"http://www.bongda.com.vn/tags/Manchester-City/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Manchester City</a>&nbsp;chạm tr&aacute;n&nbsp;<a href=\"http://www.bongda.com.vn/tags/Barcelona/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Barcelona</a>, PSG lại được dịp đối đầu với&nbsp;<a href=\"http://www.bongda.com.vn/tags/Chelsea/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Chelsea</a>. Cả hai g&atilde; nh&agrave; gi&agrave;u Man City v&agrave; PSG m&ugrave;a giải năm ngo&aacute;i đều lần lượt ng&atilde; ngựa trước Barca v&agrave; Chelsea. Cơ hội rửa hận đang đến gần hơn bao giờ hết, vấn đề l&agrave; họ c&oacute; thể tận dụng được hay kh&ocirc;ng m&agrave; th&ocirc;i.</strong></p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"http://a7.img.bongda.com.vn/wp-content/uploads/2014/12/barca5-e1418903391671.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\"Phục hận được không, hai gã nhà giàu?\" class=\"aligncenter size-full wp-image-1365117\" src=\"http://a7.img.bongda.com.vn/wp-content/uploads/2014/12/barca5-e1418903391671.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; clear:both; display:block; height:auto; list-style:none; margin:2px auto; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:620px\" /></a><br />\r\n<strong><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">M</span>an City &ndash; Barca: Nửa xanh th&agrave;nh Manchester &ldquo;kh&ocirc;n nh&agrave; dại chợ&rdquo;</strong></p>\r\n\r\n<p style=\"text-align:justify\"><a href=\"http://www.bongda.com.vn/champions-league/goc-nhin-man-city-con-khong-barca/\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">M&ugrave;a trước, Messi v&agrave; đồng đội đ&atilde; dạy cho Man City một b&agrave;i học về đẳng cấp. Ngay tr&ecirc;n s&acirc;n Etihad, Man xanh đ&atilde; để thất thủ 2 b&agrave;n</a>. Thầy tr&ograve; Pellegrini c&oacute; thể n&oacute;i rằng họ sẽ c&oacute; kết quả tốt hơn nếu như kh&ocirc;ng c&oacute; &ldquo;sự cố ngo&agrave;i &yacute; muốn&rdquo; l&agrave; chiếc thẻ đỏ của Demichelis. Nhưng pha phạm lỗi đ&oacute; của Demichelis cũng đến từ việc Barca nắm to&agrave;n bộ thế trận, đẩy City v&agrave;o thế phải chống đỡ. Lượt về, d&ugrave; rất cố gắng nhưng đại gia của b&oacute;ng đ&aacute; Anh vẫn kh&ocirc;ng thể lật ngược thế cờ.</p>\r\n\r\n<p style=\"text-align:justify\">M&ugrave;a n&agrave;y, Man City phải chờ đến v&ograve;ng đấu cuối để l&aacute;ch qua khe cửa hẹp v&agrave;o v&ograve;ng trong. Những điểm yếu của đội b&oacute;ng n&agrave;y vẫn được phơi b&agrave;y. Ở Premier League, Man City khiến mọi đối thủ phải e d&egrave;. C&ograve;n khi ra trời &Acirc;u, lại l&agrave; một h&igrave;nh ảnh đối lập ho&agrave;n to&agrave;n. Tiền c&oacute; thể cho Man xanh một đội h&igrave;nh chất lượng. Nhưng kinh nghiệm v&agrave; đẳng cấp th&igrave; kh&ocirc;ng thể mua được. C&aacute;i Man City thiếu c&ograve;n l&agrave; sự l&igrave; lợm v&agrave; bản lĩnh cần thiết.</p>\r\n\r\n<p style=\"text-align:justify\">Man City kh&ocirc;ng mất nhiều thời gian sau khi được đầu tư để c&oacute; thể xưng vương ở giải quốc nội. Nhưng đẳng cấp ch&acirc;u &Acirc;u lại l&agrave; một điều ho&agrave;n to&agrave;n kh&aacute;c. Barca c&oacute; thể phong độ của họ kh&ocirc;ng được tốt trong thời gian gần đ&acirc;y. Nhưng Messi v&agrave; đồng đội thừa sức để khiến Man City &ocirc;m hận lần nữa.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>PSG &ndash; Chelsea: Mourinho đặc biệt nguy hiểm ở m&ugrave;a b&oacute;ng thứ 2</strong></p>\r\n\r\n<p style=\"text-align:justify\">M&ugrave;a trước, PSG đ&atilde; đ&aacute;nh bại Chelsea với tỉ số 3-1 tr&ecirc;n nước Ph&aacute;p. Nhưng khi h&agrave;nh qu&acirc;n đến Stamford Bridge, đại diện của League 1 lại thất thủ 2 b&agrave;n kh&ocirc;ng gỡ. Chelsea đi tiếp nhờ luật b&agrave;n thắng tr&ecirc;n s&acirc;n kh&aacute;ch.</p>\r\n\r\n<p style=\"text-align:justify\">Phong độ của PSG hiện tại ở giải quốc nội cũng kh&ocirc;ng cao. Sau 18 v&ograve;ng đấu, họ chỉ thắng c&oacute; 10 trận, h&ograve;a tới 7 trận v&agrave; 1 lần kh&ocirc;ng c&oacute; điểm số. Chelsea cũng 1 lần bị thua trận, nhưng họ mới chỉ c&oacute; 3 trận h&ograve;a. Đội b&oacute;ng của Mourinho cũng đang dẫn đầu bảng xếp hạng với phong độ thuyết phục.</p>\r\n\r\n<p style=\"text-align:justify\">Cứ mỗi nơi m&agrave; Jose Mourinho đi qua, đội b&oacute;ng của &ocirc;ng lu&ocirc;n đặc biệt nguy hiểm ở m&ugrave;a b&oacute;ng thứ 2. B&acirc;y giờ l&agrave; thời điểm ấy. Chelsea đang bay cao tr&ecirc;n bảng xếp hạng tại Premier League. M&ugrave;a b&oacute;ng trước, một Chelsea kh&ocirc;ng thực sự mạnh vẫn vượt qua được PSG. M&ugrave;a n&agrave;y, những sự bổ sung cần thiết khiến đội h&igrave;nh của Mourinho ho&agrave;n hảo hơn. Thế n&ecirc;n, PSG cần phải đặc biệt cảnh gi&aacute;c đội b&oacute;ng đến từ London n&agrave;y.</p>\r\n', '1', '0', '2014-12-23 21:50:00', null, null, null, null, '0');
INSERT INTO `news` VALUES ('20', 'Laurent Blanc: “Tôi không dại mà đi khẩu chiến Mourinho”', '5499816eaf17e.jpg', '9', 'Gặp lại Chelsea ở vòng knock out Champions League, HLV trưởng của PSG hứa sẽ không để Mourinho kéo vào cuộc đấu trí ngoài lề.', '<p style=\"text-align:justify\"><strong>Gặp lại&nbsp;<a href=\"http://www.bongda.com.vn/tags/chelsea\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Chelsea&nbsp;</a>ở v&ograve;ng knock out Champions League, HLV trưởng của PSG hứa sẽ kh&ocirc;ng để Mourinho k&eacute;o v&agrave;o cuộc đấu tr&iacute; ngo&agrave;i lề.</strong></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1364400\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 600px;\"><a href=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/Blanc.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\"Blanc sợ hãi khi phải khẩu chiến với Mourinho\" class=\"size-full wp-image-1364400\" src=\"http://a6.img.bongda.com.vn/wp-content/uploads/2014/12/Blanc.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:590px\" /></a>\r\n\r\n<p>Blanc sợ h&atilde;i khi phải khẩu chiến với Mourinho</p>\r\n</div>\r\n\r\n<p><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">L</span><span style=\"font-family:tahoma,geneva,sans-serif; font-size:16px\">ễ bốc thăm v&ograve;ng 1/16 đ&atilde; ph&acirc;n định Chelsea đối đầu PSG tranh suất v&agrave;o tứ kết. Đo&agrave;n qu&acirc;n của Mourinho cũng chẳng lạ g&igrave; G&atilde; nh&agrave; gi&agrave;u nước Ph&aacute;p v&igrave; họ từng loại đối thủ n&agrave;y tại tứ kết m&ugrave;a trước.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Khi đ&oacute;, PSG thắng 3-1 tr&ecirc;n s&acirc;n C&ocirc;ng vi&ecirc;n c&aacute;c Ho&agrave;ng tử nhưng thua 0-2 tại Stamford Bridge, bị loại v&igrave; luật b&agrave;n thắng s&acirc;n kh&aacute;ch. HLV Laurent Blanc cho biết &ocirc;ng khao kh&aacute;t c&ugrave;ng PSG t&aacute;i đấu The Blues nhưng sẽ tr&aacute;nh xa tr&ograve; đấu khẩu với Jose Mourinho:</p>\r\n\r\n<p style=\"text-align:justify\"><em>&ldquo;T&ocirc;i kh&ocirc;ng c&oacute; tr&ograve; chơi n&agrave;o với Jose ngo&agrave;i những diễn biến trong trận đấu&rdquo;.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&ldquo;&Ocirc;ng ấy mạnh hơn t&ocirc;i ở lĩnh vực n&agrave;y (đấu khẩu). Ai cũng biết &ocirc;ng ấy nổi tiếng vụ n&agrave;y ra sao, nghe rất hay&rdquo;.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&ldquo;Jose Mourinho đ&atilde; b&agrave;y sẵn mọi thứ. Nếu bạn đ&aacute;p trả lại, đ&oacute; ch&iacute;nh l&agrave; những g&igrave; &ocirc;ng ấy mong đợi nhất&rdquo;.</em></p>\r\n\r\n<p style=\"text-align:justify\">Được biết, trận lượt đi v&ograve;ng 1/16 Champions League giữa PSG v&agrave; Chelsea sẽ diễn ra v&agrave;o ng&agrave;y 17/2 v&agrave; trận lượt về diễn ra ng&agrave;y 11/3/2015.</p>\r\n', '1', '0', '2014-12-23 21:51:26', '2014-12-30 21:55:10', '2', '0', '0', '0');
INSERT INTO `news` VALUES ('21', 'Lịch thi đấu và kết quả vòng 16 đội Champions League 2014/2015', '', '9', 'Lịch thi đấu và kết quả vòng 16 đội Champions League 2014/2015. Cập nhật liên tục!', '<p style=\"text-align:justify\"><strong>&nbsp;Lịch thi đấu v&agrave; kết quả v&ograve;ng 16 đội Champions League 2014/2015. Cập nhật li&ecirc;n tục!</strong></p>\r\n\r\n<table align=\"center\" border=\"1\" cellpadding=\"3\" id=\"table2\" style=\"border-collapse:collapse; border-radius:3px; border-spacing:0px; border:0px none; font-family:arial,bitstream vera sans,helvetica,verdana,sans-serif; font-size:16px; line-height:24px; list-style:none; margin:0px; outline:none; padding:0px; text-align:justify; width:620px\">\r\n	<tbody>\r\n		<tr>\r\n			<td colspan=\"4\" style=\"vertical-align:top\"><span style=\"color:rgb(255, 255, 255); font-family:verdana; font-size:small\"><strong>Lịch thi đấu v&agrave; kết quả v&ograve;ng 16 đội Champions League 2014/15</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"4\" style=\"vertical-align:top\"><strong><span style=\"color:rgb(255, 255, 255); font-family:verdana; font-size:9pt\">Lượt đi</span></strong></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top\"><span style=\"font-family:verdana; font-size:small\"><strong>Ng&agrave;y, giờ</strong></span></td>\r\n			<td colspan=\"3\" style=\"vertical-align:top\"><span style=\"font-family:verdana; font-size:small\"><strong>Trận đấu</strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top\"><span style=\"font-family:verdana; font-size:small\">18/02/2015, 2:45</span></td>\r\n			<td style=\"vertical-align:top\">PSG<img alt=\"\" src=\"http://www.bongda.com.vn/Data/Image/Logo/Odds/Paris_Sant%28PSG%29.gif\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; float:left; height:auto; list-style:none; margin:0px; max-width:100%; opacity:0; outline:none; padding:0px 10px; transition:opacity 0.3s ease-in; width:20px\" /></td>\r\n			<td style=\"vertical-align:top\">?:?</td>\r\n			<td style=\"vertical-align:top\"><img alt=\"\" src=\"http://www.bongda.com.vn/Data/Image/Logo/Odds/Chelsea.gif\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; float:right; height:auto; list-style:none; margin:0px; max-width:100%; opacity:0; outline:none; padding:0px 10px; transition:opacity 0.3s ease-in; width:20px\" />Chelsea</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top\"><span style=\"font-family:verdana; font-size:small\">18/02/2015, 2:45</span></td>\r\n			<td style=\"vertical-align:top\"><img alt=\"\" src=\"http://www.bongda.com.vn/Data/Image/Logo/Odds/ShakhtarDonetsk.png\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; float:left; height:auto; list-style:none; margin:0px; max-width:100%; opacity:0; outline:none; padding:0px 10px; transition:opacity 0.3s ease-in; width:20px\" />Shakhtar Donetsk</td>\r\n			<td style=\"vertical-align:top\">?:?</td>\r\n			<td style=\"vertical-align:top\"><img alt=\"\" src=\"http://www.bongda.com.vn/Data/Image/Logo/Odds/Bayern_Munich.gif\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; float:right; height:auto; list-style:none; margin:0px; max-width:100%; opacity:0; outline:none; padding:0px 10px; transition:opacity 0.3s ease-in; width:20px\" />Bayern</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:top\"><span style=\"font-family:verdana; font-size:small\">19/02/2015, 2:45</span></td>\r\n			<td style=\"vertical-align:top\"><img alt=\"\" src=\"http://www.bongda.com.vn/Data/Image/Logo/Odds/Basel.gif\" style=\"-webkit-transition:opacity 0.3s ease-in; border:0px none; float:left; height:auto; list-style:none; margin:0px; max-width:100%; opacity:0; outline:none; padding:0px 10px; transition:opacity 0.3s ease-in; width:20px\" />Basel</td>\r\n			<td style=\"vertical-align:top\">?:?</td>\r\n			<td style=\"vertical-align:top\"><img alt=\"\" src=\"http://www.bongda.com.vn/Data/Image/Logo/Odds/Porto.gif\" style=\"-webkit-transitio