INSERT INTO `news` VALUES ('22', 'Gerard Pique chọn đội hình tiêu biểu: Có Persie và Ronaldo', '5499825cc5c1f.jpg', '7', 'Trong đội hình tiêu biểu được trung vệ Gerard Pique lựa chọn, phần đông là những cầu thủ của Barca, ĐT Tây Ban Nha cùng 2 ngôi sao Van Persie, Cristiano Ronaldo.', '<p style=\"text-align:justify\"><strong>Trong đội h&igrave;nh ti&ecirc;u biểu được trung vệ&nbsp;<a href=\"http://beta.bongda.com.vn/tags/gerard-pique\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Gerard Pique</a>&nbsp;lựa chọn, phần đ&ocirc;ng l&agrave; những cầu thủ của Barca, ĐT T&acirc;y Ban Nha c&ugrave;ng 2 ng&ocirc;i sao&nbsp;<a href=\"http://beta.bongda.com.vn/tags/robin-van-persie\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\" target=\"_blank\">Van Persie</a>, Cristiano Ronaldo.</strong></p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_1341490\" style=\"padding: 0px; margin: 0px auto 20px; outline: none; list-style: none; border: 0px none; clear: both; line-height: 18px; text-align: center; max-width: 100%; font-family: Tahoma, Geneva, sans-serif; font-size: 16px; width: 630px;\"><a href=\"http://a7.img.bongda.com.vn/wp-content/uploads/2014/11/PiqueS.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(209, 47, 47); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out;\"><img alt=\"Pique đưa Ronaldo, Persie vào ĐHTB\" class=\"size-full wp-image-1341490\" src=\"http://a7.img.bongda.com.vn/wp-content/uploads/2014/11/PiqueS.jpg\" style=\"-webkit-transition:opacity 0.3s ease-in; border-width:0px; height:auto; list-style:none; margin:0px; max-width:100%; opacity:1; outline:none; padding:0px; transition:opacity 0.3s ease-in; width:620px\" /></a>\r\n\r\n<p>Pique đưa Ronaldo, Persie v&agrave;o ĐHTB</p>\r\n</div>\r\n\r\n<p><span style=\"font-family:georgia,arial,helvetica neue,helvetica,verdana,sans-serif; font-size:45px\">T</span><span style=\"font-family:tahoma,geneva,sans-serif; font-size:16px\">hủ m&ocirc;n trong đội h&igrave;nh ưa th&iacute;ch của Pique kh&ocirc;ng ai kh&aacute;c l&agrave; thủ th&agrave;nh số 1 của ĐT T&acirc;y Ban Nha Iker Casillas.</span></p>\r\n\r\n<p style=\"text-align:justify\">Trong khi đ&oacute;, h&agrave;ng thủ 3 người ở đội h&igrave;nh n&agrave;y đều l&agrave; 3 cầu thủ của Barca gồm Jordi Alba, Dani Alves v&agrave; ch&iacute;nh Pique.</p>\r\n\r\n<p style=\"text-align:justify\">Chơi thấp nhất nơi h&agrave;ng tiền vệ 4 người l&agrave; Sergio Busquest. 3 c&aacute;i t&ecirc;n c&ograve;n lại l&agrave; Iniesta, Fabregas c&ugrave;ng Toni Kroos của Real Madrid v&agrave; tuyển Đức.</p>\r\n\r\n<p style=\"text-align:justify\">Ronaldo, Messi v&agrave; Van Persie l&agrave; h&agrave;ng tiền đạo 3 người được Pique lựa chọn.</p>\r\n\r\n<p style=\"text-align:justify\">Nh&igrave;n lại, đội h&igrave;nh ưa th&iacute;ch của Barca thực sự l&agrave; dream team khi c&oacute; được sự c&acirc;n bằng giữa tấn c&ocirc;ng v&agrave; ph&ograve;ng thủ.</p>\r\n', '1', '0', '2014-12-23 21:55:24', null, '2', '0', '0', '0');
